## Decorations & Inline Features

### Decoration Provider with Real-time Updates

```typescript
// src/decorationProvider.ts
import * as vscode from 'vscode';

export class DecorationProvider {
  private errorDecoration: vscode.TextEditorDecorationType;
  private warningDecoration: vscode.TextEditorDecorationType;
  private infoDecoration: vscode.TextEditorDecorationType;

  constructor() {
    this.errorDecoration = vscode.window.createTextEditorDecorationType({
      backgroundColor: 'rgba(255, 0, 0, 0.2)',
      border: '1px solid red',
      overviewRulerColor: 'red',
      overviewRulerLane: vscode.OverviewRulerLane.Right,
      isWholeLine: true,
    });

    this.warningDecoration = vscode.window.createTextEditorDecorationType({
      backgroundColor: 'rgba(255, 165, 0, 0.2)',
      border: '1px solid orange',
      overviewRulerColor: 'orange',
      overviewRulerLane: vscode.OverviewRulerLane.Right,
    });

    this.infoDecoration = vscode.window.createTextEditorDecorationType({
      backgroundColor: 'rgba(0, 100, 255, 0.1)',
      border: '1px solid blue',
    });
  }

  updateDecorations(editor: vscode.TextEditor) {
    if (!editor) return;

    const document = editor.document;
    const errorRanges: vscode.Range[] = [];
    const warningRanges: vscode.Range[] = [];
    const infoRanges: vscode.Range[] = [];

    // Analyze document
    const text = document.getText();
    const lines = text.split('\n');

    lines.forEach((line, lineNum) => {
      // Example: highlight console.log (warning)
      if (line.includes('console.log')) {
        const startCol = line.indexOf('console.log');
        const endCol = startCol + 'console.log'.length;

        warningRanges.push(
          new vscode.Range(lineNum, startCol, lineNum, endCol)
        );
      }

      // Example: highlight TODO (info)
      if (line.includes('TODO')) {
        const startCol = line.indexOf('TODO');
        const endCol = startCol + 'TODO'.length;

        infoRanges.push(
          new vscode.Range(lineNum, startCol, lineNum, endCol)
        );
      }

      // Example: highlight var declarations (error)
      if (/^\s*var\s+/.test(line)) {
        const startCol = line.indexOf('var');
        const endCol = startCol + 'var'.length;

        errorRanges.push(
          new vscode.Range(lineNum, startCol, lineNum, endCol)
        );
      }
    });

    // Apply decorations
    editor.setDecorations(this.errorDecoration, errorRanges);
    editor.setDecorations(this.warningDecoration, warningRanges);
    editor.setDecorations(this.infoDecoration, infoRanges);
  }

  dispose() {
    this.errorDecoration.dispose();
    this.warningDecoration.dispose();
    this.infoDecoration.dispose();
  }
}

// Register and manage decorations
export function registerDecorationProvider(context: vscode.ExtensionContext) {
  const provider = new DecorationProvider();

  // Update decorations for active editor
  if (vscode.window.activeTextEditor) {
    provider.updateDecorations(vscode.window.activeTextEditor);
  }

  // Update on document change
  vscode.workspace.onDidChangeTextDocument(
    (event) => {
      for (const editor of vscode.window.visibleTextEditors) {
        if (editor.document === event.document) {
          provider.updateDecorations(editor);
        }
      }
    },
    null,
    context.subscriptions
  );

  // Update on active editor change
  vscode.window.onDidChangeActiveTextEditor(
    (editor) => {
      if (editor) {
        provider.updateDecorations(editor);
      }
    },
    null,
    context.subscriptions
  );

  context.subscriptions.push(provider);
}
```

### CodeLens Provider

```typescript
// src/codelensProvider.ts
import * as vscode from 'vscode';

export class TestCodeLensProvider implements vscode.CodeLensProvider {
  private onDidChangeCodeLensesEmitter =
    new vscode.EventEmitter<void>();
  public onDidChangeCodeLenses =
    this.onDidChangeCodeLensesEmitter.event;

  private regex = /test\s*\(\s*["']([^"']*)["']/g;

  provideCodeLenses(
    document: vscode.TextDocument
  ): vscode.CodeLens[] {
    const lenses: vscode.CodeLens[] = [];
    const text = document.getText();

    let match;
    while ((match = this.regex.exec(text))) {
      const startPos = document.positionAt(match.index);
      const endPos = document.positionAt(match.index + match[0].length);
      const range = new vscode.Range(startPos, endPos);

      const testName = match[1];

      // Run test CodeLens
      const runLens = new vscode.CodeLens(range);
      runLens.command = {
        title: 'Run Test',
        command: 'extension.runTest',
        arguments: [testName],
      };

      // Debug test CodeLens
      const debugLens = new vscode.CodeLens(
        new vscode.Range(
          startPos.line,
          startPos.character + 5,
          startPos.line,
          startPos.character + 5
        )
      );
      debugLens.command = {
        title: 'Debug Test',
        command: 'extension.debugTest',
        arguments: [testName],
      };

      lenses.push(runLens, debugLens);
    }

    return lenses;
  }

  async resolveCodeLens(
    codeLens: vscode.CodeLens
  ): Promise<vscode.CodeLens> {
    // Optionally enrich lens with dynamic data
    if (codeLens.command?.command === 'extension.runTest') {
      const testName = codeLens.command.arguments?.[0];
      // Could fetch test status here
      codeLens.command.title = `‚ñ∂ Run: ${testName}`;
    }
    return codeLens;
  }
}

export function registerCodeLensProvider(context: vscode.ExtensionContext) {
  const provider = new TestCodeLensProvider();

  context.subscriptions.push(
    vscode.languages.registerCodeLensProvider(
      { scheme: 'file', language: 'javascript' },
      provider
    )
  );

  // Register test commands
  context.subscriptions.push(
    vscode.commands.registerCommand(
      'extension.runTest',
      (testName: string) => {
        vscode.window.showInformationMessage(`Running test: ${testName}`);
      }
    ),
    vscode.commands.registerCommand(
      'extension.debugTest',
      (testName: string) => {
        vscode.window.showInformationMessage(`Debugging test: ${testName}`);
      }
    )
  );
}
