```typescript
// src/codelensProvider.ts
import * as vscode from 'vscode';

export class TestCodeLensProvider implements vscode.CodeLensProvider {
  private onDidChangeCodeLensesEmitter =
    new vscode.EventEmitter<void>();
  public onDidChangeCodeLenses =
    this.onDidChangeCodeLensesEmitter.event;

  private regex = /test\s*\(\s*["']([^"']*)["']/g;

  provideCodeLenses(
    document: vscode.TextDocument
  ): vscode.CodeLens[] {
    const lenses: vscode.CodeLens[] = [];
    const text = document.getText();

    let match;
    while ((match = this.regex.exec(text))) {
      const startPos = document.positionAt(match.index);
      const endPos = document.positionAt(match.index + match[0].length);
      const range = new vscode.Range(startPos, endPos);

      const testName = match[1];

      // Run test CodeLens
      const runLens = new vscode.CodeLens(range);
      runLens.command = {
        title: 'Run Test',
        command: 'extension.runTest',
        arguments: [testName],
      };

      // Debug test CodeLens
      const debugLens = new vscode.CodeLens(
        new vscode.Range(
          startPos.line,
          startPos.character + 5,
          startPos.line,
          startPos.character + 5
        )
      );
      debugLens.command = {
        title: 'Debug Test',
        command: 'extension.debugTest',
        arguments: [testName],
      };

      lenses.push(runLens, debugLens);
    }

    return lenses;
  }

  async resolveCodeLens(
    codeLens: vscode.CodeLens
  ): Promise<vscode.CodeLens> {
    // Optionally enrich lens with dynamic data
    if (codeLens.command?.command === 'extension.runTest') {
      const testName = codeLens.command.arguments?.[0];
      // Could fetch test status here
      codeLens.command.title = `‚ñ∂ Run: ${testName}`;
    }
    return codeLens;
  }
}

export function registerCodeLensProvider(context: vscode.ExtensionContext) {
  const provider = new TestCodeLensProvider();

  context.subscriptions.push(
    vscode.languages.registerCodeLensProvider(
      { scheme: 'file', language: 'javascript' },
      provider
    )
  );

  // Register test commands
  context.subscriptions.push(
    vscode.commands.registerCommand(
      'extension.runTest',
      (testName: string) => {
        vscode.window.showInformationMessage(`Running test: ${testName}`);
      }
    ),
    vscode.commands.registerCommand(
      'extension.debugTest',
      (testName: string) => {
        vscode.window.showInformationMessage(`Debugging test: ${testName}`);
      }
    )
  );
}
