### Complete Webview Panel Example

```typescript
// src/webviewPanel.ts
import * as vscode from 'vscode';
import * as path from 'path';

export class MyWebviewPanel {
  public static currentPanel: MyWebviewPanel | undefined;

  private readonly panel: vscode.WebviewPanel;
  private readonly extensionUri: vscode.Uri;
  private disposables: vscode.Disposable[] = [];

  public static createOrShow(extensionUri: vscode.Uri) {
    const column = vscode.window.activeTextEditor
      ? vscode.window.activeTextEditor.viewColumn
      : undefined;

    // If panel exists, show it
    if (MyWebviewPanel.currentPanel) {
      MyWebviewPanel.currentPanel.panel.reveal(column);
      return;
    }

    // Create new panel
    const panel = vscode.window.createWebviewPanel(
      'myWebview',
      'My Panel',
      column || vscode.ViewColumn.One,
      {
        enableScripts: true,
        localResourceRoots: [vscode.Uri.joinPath(extensionUri, 'media')],
        retainContextWhenHidden: true,
      }
    );

    MyWebviewPanel.currentPanel = new MyWebviewPanel(panel, extensionUri);
  }

  public static revive(
    panel: vscode.WebviewPanel,
    extensionUri: vscode.Uri
  ) {
    MyWebviewPanel.currentPanel = new MyWebviewPanel(panel, extensionUri);
  }

  private constructor(
    panel: vscode.WebviewPanel,
    extensionUri: vscode.Uri
  ) {
    this.panel = panel;
    this.extensionUri = extensionUri;

    this.update();

    this.panel.onDidDispose(
      () => {
        MyWebviewPanel.currentPanel = undefined;
        this.dispose();
      },
      null,
      this.disposables
    );

    this.panel.onDidChangeViewState(
      (event) => {
        if (event.webviewPanel.visible) {
          this.update();
        }
      },
      null,
      this.disposables
    );

    this.panel.webview.onDidReceiveMessage(
      (message) => {
        this.handleMessage(message);
      },
      null,
      this.disposables
    );
  }

  public update() {
    this.panel.webview.html = this.getHtml();
  }

  private handleMessage(message: any) {
    const command = message.command;

    switch (command) {
      case 'alert':
        vscode.window.showInformationMessage(message.text);
        return;
      case 'analyze':
        this.analyzeCode(message.code);
        return;
    }
  }

  private async analyzeCode(code: string) {
    // Simulate analysis
    const results = {
      errors: 2,
      warnings: 5,
      suggestions: 3,
    };

    // Send results back to webview
    this.panel.webview.postMessage({
      command: 'analysisResults',
      results,
    });
  }

  private getHtml(): string {
    const scriptUri = this.panel.webview.asWebviewUri(
      vscode.Uri.joinPath(this.extensionUri, 'media', 'main.js')
    );

    const styleUri = this.panel.webview.asWebviewUri(
      vscode.Uri.joinPath(this.extensionUri, 'media', 'styles.css')
    );

    return `
      <!DOCTYPE html>
      <html>
      <head>
        <meta charset="UTF-8">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <title>My Webview</title>
        <link rel="stylesheet" href="${styleUri}">
      </head>
      <body>
        <h1>Code Analysis Panel</h1>

        <textarea id="codeInput" placeholder="Paste code here..." rows="10">
function hello() {
  console.log('Hello, world!');
}
        </textarea>

        <button id="analyzeBtn">Analyze</button>

        <div id="results"></div>

        <script src="${scriptUri}"></script>
      </body>
      </html>
    `;
  }

  public dispose() {
    MyWebviewPanel.currentPanel = undefined;

    this.panel.dispose();

    while (this.disposables.length) {
      const disposable = this.disposables.pop();
      if (disposable) {
        disposable.dispose();
      }
    }
  }
}

// Register webview command
export function registerWebviewCommand(context: vscode.ExtensionContext) {
  context.subscriptions.push(
    vscode.commands.registerCommand('extension.openWebview', () => {
      MyWebviewPanel.createOrShow(context.extensionUri);
    })
  );
}
