     1→/**
     2→ * SPLICE CEP Panel Styles
     3→ * Premium "Deep Space" Theme
     4→ */
     5→
     6→:root {
     7→    /* Palette */
     8→    --bg-core: #121212;
     9→    --bg-panel: var(--bg-panel);
    10→    --bg-card: rgba(30, 30, 30, 0.6);
    11→    --glass-border: rgba(255, 255, 255, 0.08);
    12→    --glass-highlight: rgba(255, 255, 255, 0.03);
    13→
    14→    /* Accents */
    15→    --accent-primary: var(--accent-primary);
    16→    --accent-gradient: linear-gradient(135deg, var(--accent-primary), #a64dff);
    17→    --accent-glow: 0 0 20px rgba(74, 158, 255, 0.15);
    18→
    19→    /* Functional Colors */
    20→    --text-main: #f0f0f0;
    21→    --text-muted: var(--text-muted)888;
    22→    --success: #00e676;
    23→    --error: #ff5252;
    24→
    25→    /* Spacing & Radius */
    26→    --radius-sm: 6px;
    27→    --radius-md: 12px;
    28→    --radius-lg: 16px;
    29→    --spacing-xs: 4px;
    30→    --spacing-sm: 8px;
    31→    --spacing-md: 16px;
    32→
    33→    /* Transitions */
    34→    --ease-smooth: cubic-bezier(0.4, 0.0, 0.2, 1);
    35→}
    36→
    37→/* Reset & Base */
    38→* {
    39→    box-sizing: border-box;
    40→    margin: 0;
    41→    padding: 0;
    42→}
    43→
    44→body {
    45→    font-family: 'Inter', -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
    46→    background: var(--bg-core);
    47→    color: var(--text-main);
    48→    margin: 0;
    49→    padding: var(--spacing-md);
    50→    height: 100vh;
    51→    overflow-y: auto;
    52→    overflow-x: hidden;
    53→    font-size: 12px;
    54→    line-height: 1.5;
    55→    -webkit-font-smoothing: antialiased;
    56→}
    57→
    58→/* Loading Overlay */
    59→#loading-overlay {
    60→    position: fixed;
    61→    top: 0;
    62→    left: 0;
    63→    width: 100%;
    64→    height: 100%;
    65→    background: var(--bg-core);
    66→    z-index: 9999;
    67→    display: flex;
    68→    flex-direction: column;
    69→    align-items: center;
    70→    justify-content: center;
    71→    transition: opacity 0.5s var(--ease-smooth), visibility 0.5s;
    72→}
    73→
    74→#loading-overlay.hidden {
    75→    opacity: 0;
    76→    visibility: hidden;
    77→    pointer-events: none;
    78→}
    79→
    80→.loader-logo {
    81→    width: 48px;
    82→    height: 48px;
    83→    border-radius: 50%;
    84→    background: var(--accent-gradient);
    85→    animation: pulse-ring 2s infinite;
    86→    margin-bottom: var(--spacing-md);
    87→    box-shadow: var(--accent-glow);
    88→}
    89→
    90→.loader-text {
    91→    font-size: 11px;
    92→    color: var(--text-muted);
    93→    letter-spacing: 1px;
    94→    text-transform: uppercase;
    95→    font-weight: 500;
    96→}
    97→
    98→@keyframes pulse-ring {
    99→    0% {
   100→        transform: scale(0.95);
   101→        box-shadow: 0 0 0 0 rgba(74, 158, 255, 0.7);
   102→    }
   103→
   104→    70% {
   105→        transform: scale(1);
   106→        box-shadow: 0 0 0 10px rgba(74, 158, 255, 0);
   107→    }
   108→
   109→    100% {
   110→        transform: scale(0.95);
   111→        box-shadow: 0 0 0 0 rgba(74, 158, 255, 0);
   112→    }
   113→}
   114→
   115→/* Scrollbar */
   116→::-webkit-scrollbar {
   117→    width: 6px;
   118→}
   119→
   120→::-webkit-scrollbar-track {
   121→    background: transparent;
   122→}
   123→
   124→::-webkit-scrollbar-thumb {
   125→    background: rgba(255, 255, 255, 0.1);
   126→    border-radius: 3px;
   127→}
   128→
   129→::-webkit-scrollbar-thumb:hover {
   130→    background: rgba(255, 255, 255, 0.2);
   131→}
   132→
   133→/* Base UI Components */
   134→button {
   135→    font-family: inherit;
   136→}
   137→
   138→/* Header */
   139→.header {
   140→    display: flex;
   141→    justify-content: space-between;
   142→    align-items: center;
   143→    margin-bottom: var(--spacing-md);
   144→    padding-bottom: var(--spacing-sm);
   145→    border-bottom: 1px solid var(--glass-border);
   146→}
   147→
   148→.header h1 {
   149→    font-size: 16px;
   150→    font-weight: 700;
   151→    margin: 0;
   152→    background: var(--accent-gradient);
   153→    -webkit-background-clip: text;
   154→    background-clip: text;
   155→    -webkit-text-fill-color: transparent;
   156→    letter-spacing: -0.5px;
   157→}
   158→
   159→.header-actions {
   160→    display: flex;
   161→    gap: var(--spacing-sm);
   162→    align-items: center;
   163→}
   164→
   165→/* Credit Badge */
   166→.credit-badge {
   167→    font-size: 10px;
   168→    font-weight: 600;
   169→    background: var(--bg-panel);
   170→    border: 1px solid var(--glass-border);
   171→    border-radius: 8px;
   172→    padding: 4px 8px;
   173→    cursor: pointer;
   174→    transition: all 0.2s var(--ease-smooth);
   175→}
   176→
   177→.credit-badge:hover {
   178→    border-color: var(--accent-primary);
   179→}
   180→
   181→.credit-badge.login {
   182→    color: var(--accent-primary);
   183→}
   184→
   185→.credit-badge.ok {
   186→    color: #4ade80;
   187→}
   188→
   189→.credit-badge.low {
   190→    color: #f97316;
   191→    border-color: #f97316;
   192→}
   193→
   194→.credit-badge.error {
   195→    color: #ef4444;
   196→    border-color: #ef4444;
   197→}
   198→
   199→.credit-badge.trial {
   200→    color: #a855f7;
   201→    background: rgba(168, 85, 247, 0.1);
   202→    border-color: rgba(168, 85, 247, 0.3);
   203→}
   204→
   205→.credit-badge.retrying {
   206→    color: #f59e0b;
   207→    border-color: #f59e0b;
   208→    animation: pulse 1s ease-in-out infinite;
   209→}
   210→
   211→@keyframes pulse {
   212→    0%, 100% { opacity: 1; }
   213→    50% { opacity: 0.5; }
   214→}
   215→
   216→/* Action Card */
   217→.action-card {
   218→    background: var(--bg-card);
   219→    backdrop-filter: blur(10px);
   220→    border: 1px solid var(--glass-border);
   221→    border-radius: var(--radius-md);
   222→    padding: var(--spacing-md);
   223→    margin-bottom: var(--spacing-md);
   224→    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.2);
   225→    transition: transform 0.2s var(--ease-smooth);
   226→}
   227→
   228→.action-card h2 {
   229→    font-size: 10px;
   230→    font-weight: 700;
   231→    margin: 0 0 var(--spacing-md) 0;
   232→    color: var(--text-muted);
   233→    text-transform: uppercase;
   234→    letter-spacing: 1px;
   235→}
   236→
   237→/* GO Button */
   238→.go-btn {
   239→    width: 100%;
   240→    padding: 16px;
   241→    font-size: 14px;
   242→    font-weight: 600;
   243→    background: var(--accent-gradient);
   244→    border: none;
   245→    border-radius: var(--radius-md);
   246→    color: white;
   247→    cursor: pointer;
   248→    margin-bottom: var(--spacing-sm);
   249→    letter-spacing: 0.5px;
   250→    transition: transform 0.2s var(--ease-smooth), box-shadow 0.2s;
   251→    box-shadow: var(--accent-glow);
   252→}
   253→
   254→.go-btn:hover {
   255→    transform: translateY(-2px);
   256→    box-shadow: 0 4px 15px rgba(74, 158, 255, 0.3);
   257→}
   258→
   259→.go-btn:active {
   260→    transform: translateY(0);
   261→}
   262→
   263→.go-btn:disabled {
   264→    opacity: 0.5;
   265→    cursor: not-allowed;
   266→    transform: none;
   267→    box-shadow: none;
   268→}
   269→
   270→/* Icon Buttons */
   271→.icon-btn {
   272→    background: transparent;
   273→    border: 1px solid var(--glass-border);
   274→    color: var(--text-muted);
   275→    width: 32px;
   276→    height: 32px;
   277→    border-radius: var(--radius-sm);
   278→    cursor: pointer;
   279→    font-size: 14px;
   280→    display: flex;
   281→    align-items: center;
   282→    justify-content: center;
   283→    transition: all 0.2s var(--ease-smooth);
   284→}
   285→
   286→.icon-btn:hover {
   287→    background: var(--glass-highlight);
   288→    color: var(--text-main);
   289→    border-color: var(--text-muted);
   290→}
   291→
   292→/* Options Toggle */
   293→.options-toggle {
   294→    background: transparent;
   295→    border: none;
   296→    color: var(--text-muted);
   297→    font-size: 10px;
   298→    cursor: pointer;
   299→    padding: 4px 0;
   300→    width: 100%;
   301→    text-align: left;
   302→}
   303→
   304→.options-toggle:hover {
   305→    color: #aaa;
   306→}
   307→
   308→.options-toggle::before {
   309→    content: '+';
   310→    margin-right: 4px;
   311→    font-weight: bold;
   312→}
   313→
   314→.options-toggle.expanded::before {
   315→    content: '-';
   316→}
   317→
   318→/* Options Panel */
   319→.options-panel {
   320→    margin-top: 8px;
   321→    padding-top: 8px;
   322→    border-top: 1px solid var(--glass-border);
   323→}
   324→
   325→.options-panel.collapsed,
   326→.sub-options.collapsed {
   327→    display: none;
   328→}
   329→
   330→/* Form Elements */
   331→.form-group {
   332→    margin-bottom: 10px;
   333→}
   334→
   335→.form-group label {
   336→    display: block;
   337→    font-size: 10px;
   338→    color: var(--text-muted);
   339→    margin-bottom: 4px;
   340→}
   341→
   342→.form-group.small {
   343→    margin-bottom: 6px;
   344→}
   345→
   346→.form-group.small label {
   347→    font-size: 9px;
   348→}
   349→
   350→input[type="text"],
   351→input[type="email"],
   352→input[type="number"],
   353→select {
   354→    width: 100%;
   355→    padding: 6px 8px;
   356→    background: var(--bg-panel);
   357→    border: 1px solid var(--glass-border);
   358→    border-radius: 4px;
   359→    color: #e1e1e1;
   360→    font-size: 11px;
   361→}
   362→
   363→input[type="text"]:focus,
   364→input[type="email"]:focus,
   365→input[type="number"]:focus,
   366→select:focus {
