   368→    border-color: var(--accent-primary);
   369→}
   370→
   371→/* Slider */
   372→.slider-container {
   373→    margin-bottom: var(--spacing-md);
   374→}
   375→
   376→.slider-container label {
   377→    display: block;
   378→    font-size: 11px;
   379→    color: var(--text-muted);
   380→    margin-bottom: var(--spacing-xs);
   381→    font-weight: 500;
   382→}
   383→
   384→.slider-container.small {
   385→    margin-bottom: var(--spacing-sm);
   386→}
   387→
   388→input[type="range"] {
   389→    width: 100%;
   390→    height: 4px;
   391→    background: rgba(255, 255, 255, 0.1);
   392→    border-radius: 2px;
   393→    outline: none;
   394→    -webkit-appearance: none;
   395→    appearance: none;
   396→    /* Fixed lint: standard property */
   397→    cursor: pointer;
   398→}
   399→
   400→input[type="range"]::-webkit-slider-thumb {
   401→    -webkit-appearance: none;
   402→    appearance: none;
   403→    /* Fixed lint */
   404→    width: 16px;
   405→    height: 16px;
   406→    background: var(--accent-primary);
   407→    border-radius: 50%;
   408→    cursor: pointer;
   409→    box-shadow: 0 0 10px rgba(74, 158, 255, 0.4);
   410→    transition: transform 0.2s var(--ease-smooth);
   411→    margin-top: -6px;
   412→    /* Vertical centering adjustment */
   413→}
   414→
   415→input[type="range"]::-webkit-slider-thumb:hover {
   416→    transform: scale(1.2);
   417→}
   418→
   419→input[type="range"]::-webkit-slider-runnable-track {
   420→    width: 100%;
   421→    height: 4px;
   422→    cursor: pointer;
   423→    background: rgba(255, 255, 255, 0.1);
   424→    border-radius: 2px;
   425→}
   426→
   427→.slider-labels {
   428→    display: flex;
   429→    justify-content: space-between;
   430→    font-size: 10px;
   431→    color: var(--text-muted);
