   434→
   435→/* Checkbox */
   436→.checkbox-label {
   437→    display: flex;
   438→    align-items: center;
   439→    gap: 8px;
   440→    font-size: 11px;
   441→    color: var(--text-main);
   442→    cursor: pointer;
   443→    margin-bottom: var(--spacing-sm);
   444→    user-select: none;
   445→}
   446→
   447→.checkbox-label input[type="checkbox"] {
   448→    width: 16px;
   449→    height: 16px;
   450→    accent-color: var(--accent-primary);
   451→    background: rgba(0, 0, 0, 0.3);
   452→    border: 1px solid var(--glass-border);
   453→    border-radius: 4px;
   454→    cursor: pointer;
