   456→
   457→/* Tier Badge */
   458→.tier-badge {
   459→    font-size: 9px;
   460→    background: var(--accent-gradient);
   461→    color: white;
   462→    padding: 2px 6px;
   463→    border-radius: var(--radius-sm);
