   531→    margin-top: 8px;
   532→}
   533→
   534→.preview-section.hidden {
   535→    display: none;
   536→}
   537→
   538→.preview-header {
   539→    display: flex;
   540→    justify-content: space-between;
   541→    align-items: center;
   542→    margin-bottom: 8px;
   543→}
   544→
   545→.preview-header h3 {
   546→    font-size: 11px;
   547→    font-weight: 600;
   548→    color: #aaa;
   549→    text-transform: uppercase;
   550→}
   551→
   552→.preview-counts {
   553→    display: flex;
   554→    gap: 10px;
   555→    font-size: 10px;
   556→    color: #666;
   557→}
   558→
   559→.preview-counts span span {
   560→    color: var(--accent-primary);
   561→    font-weight: 600;
   562→}
   563→
   564→.preview-controls {
   565→    margin-bottom: 8px;
   566→}
   567→
   568→.preview-list {
   569→    max-height: 200px;
   570→    overflow-y: auto;
   571→    margin-bottom: var(--spacing-md);
   572→    padding-right: 4px;
   573→    /* Space for scrollbar */
   574→}
   575→
   576→.preview-item {
   577→    display: flex;
   578→    align-items: center;
   579→    padding: 8px 12px;
   580→    margin-bottom: 6px;
   581→    background: rgba(30, 30, 30, 0.4);
   582→    border: 1px solid var(--glass-border);
   583→    border-radius: var(--radius-sm);
   584→    border-left: 3px solid var(--accent-primary);
   585→    cursor: pointer;
   586→    transition: all 0.15s var(--ease-smooth);
   587→}
   588→
   589→.preview-item:hover {
   590→    background: rgba(255, 255, 255, 0.05);
   591→    transform: translateX(2px);
   592→}
   593→
   594→.preview-item.excluded {
   595→    opacity: 0.5;
   596→    border-left-color: var(--text-muted);
