   608→    accent-color: var(--accent-primary);
   609→}
   610→
   611→.preview-item-icon {
   612→    margin-right: 10px;
   613→    font-size: 14px;
   614→}
   615→
   616→.preview-item-info {
   617→    flex: 1;
   618→    min-width: 0;
   619→}
   620→
   621→.preview-item-time {
   622→    font-size: 11px;
   623→    font-weight: 500;
   624→    color: #e1e1e1;
