   632→}
   633→
   634→.preview-actions {
   635→    display: flex;
   636→    gap: 8px;
   637→}
   638→
   639→/* Buttons */
   640→.btn {
   641→    padding: 8px 16px;
   642→    font-size: 12px;
   643→    font-weight: 500;
   644→    border: none;
   645→    border-radius: var(--radius-sm);
