   685→    font-size: 11px;
   686→}
   687→
   688→/* Chapter Results */
   689→.chapter-results,
   690→.zoom-results {
   691→    margin-top: 12px;
   692→    padding-top: 12px;
   693→    border-top: 1px solid #333;
   694→}
   695→
   696→.chapter-results.hidden,
   697→.zoom-results.hidden {
   698→    display: none;
