   700→
   701→.chapter-header,
   702→.zoom-header {
   703→    display: flex;
   704→    justify-content: space-between;
   705→    align-items: center;
   706→    margin-bottom: 8px;
   707→}
   708→
   709→.chapter-header h3,
   710→.zoom-header h3 {
   711→    font-size: 11px;
   712→    font-weight: 600;
   713→    color: #aaa;
   714→    text-transform: uppercase;
