  1313→    if (!email) {
  1314→        setStatus('Please enter your email address', true);
  1315→        return;
  1316→    }
  1317→
  1318→    // Basic email validation
  1319→    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1320→    if (!emailRegex.test(email)) {
  1321→        setStatus('Please enter a valid email address', true);
  1322→        return;
  1323→    }
  1324→
  1325→    const lookupBtn = document.getElementById('lookupLicenseBtn');
  1326→    if (lookupBtn) {
