   746→                    body: JSON.stringify({ key: licenseKey })
   747→                }, SPLICE_CONFIG.FETCH_TIMEOUT);
   748→
   749→                const result = await response.json();
   750→
   751→                if (!result.success) {
   752→                    showLoginError(result.error || 'Activation failed');
   753→                    return;
