  1099→function addJobToList(jobId, options, isSceneAware = false) {
  1100→  const job = {
  1101→    jobId,
  1102→    status: 'pending',
  1103→    progress: 0,
  1104→    mood: options.mood,
  1105→    duration: options.duration,
  1106→    isSceneAware,
  1107→    createdAt: new Date().toISOString()
  1108→  };
  1109→  musicState.jobs.unshift(job);
  1110→  renderJobsList();
