   416→function initPresetSelector() {
   417→    if (!ui.presetSelector) return;
   418→
   419→    ui.presetSelector.addEventListener('change', () => {
   420→        const presetId = ui.presetSelector.value;
   421→        applyPreset(presetId);
   422→    });
