  1028→async function buildSequence() {
  1029→    if (isOperationInProgress) return;
  1030→
  1031→    isOperationInProgress = true;
  1032→    setStatus('Building sequence...');
  1033→
  1034→    try {
  1035→        const settings = getSettings();
  1036→
  1037→        // Ensure we have sequence info
  1038→        if (!currentSequenceInfo) {
  1039→            currentSequenceInfo = await jsx.call('getActiveSequence');
  1040→        }
  1041→
  1042→        // Create cut list from selected silences
  1043→        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
  1044→
  1045→        // Calculate total duration from sequence info or last silence
  1046→        let duration = 0;
  1047→        if (currentSequenceInfo && currentSequenceInfo.duration) {
  1048→            duration = currentSequenceInfo.duration;
  1049→        } else if (previewSilences.length > 0) {
  1050→            const lastSilence = previewSilences[previewSilences.length - 1];
  1051→            duration = lastSilence.end + 10; // Add buffer
  1052→        }
  1053→
  1054→        // Call backend to generate cut list
  1055→        const cutListResponse = await fetchWithTimeout(
  1056→            `${getBackendUrl()}/cut-list`,
  1057→            {
  1058→                method: 'POST',
  1059→                headers: getAuthHeaders(),
  1060→                body: JSON.stringify({
  1061→                    sourceName: currentSequenceInfo?.name || 'Untitled Sequence',
  1062→                    sourcePath: currentSequenceInfo?.treePath || currentSequenceInfo?.name || 'Untitled',
  1063→                    duration: duration,
  1064→                    silences: selectedSilences,
  1065→                    takes: previewTakes,
  1066→                    enableTakes: settings.enableTakesDetection,
  1067→                    jCutOffset: settings.enableJCut ? settings.jcutLeadIn : 0,
  1068→                    lCutOffset: settings.enableJCut ? settings.jcutLeadOut : 0
  1069→                })
  1070→            }
  1071→        );
  1072→
  1073→        if (!cutListResponse.ok) {
  1074→            throw new Error(await parseErrorResponse(cutListResponse));
  1075→        }
  1076→
  1077→        const cutList = await cutListResponse.json();
  1078→
  1079→        // Build sequence via JSX
  1080→        const result = await jsx.call('buildSequenceFromCutList', JSON.stringify(cutList));
  1081→
  1082→        if (result && result.success) {
  1083→            setStatus(`Built sequence: ${result.sequenceName}`);
  1084→            hidePreview();
  1085→        } else {
  1086→            throw new Error(result?.error || 'Build failed');
  1087→        }
  1088→    } catch (error) {
  1089→        setStatus('Build failed: ' + error.message, true);
  1090→    } finally {
  1091→        isOperationInProgress = false;
  1092→    }
  1093→}
