  1368→async function updateCredits() {
  1369→    if (!ui.creditBadge) return;
  1370→
  1371→    const settings = getSettings();
  1372→    if (!settings.customerId) {
  1373→        ui.creditBadge.className = 'credit-badge login';
  1374→        ui.creditBadge.textContent = 'Login';
  1375→        ui.creditBadge.style.display = 'flex';
  1376→        return;
  1377→    }
  1378→
  1379→    try {
  1380→        const response = await fetchWithTimeout(
  1381→            `${getBackendUrl()}/credits`,
  1382→            {
  1383→                method: 'GET',
  1384→                headers: getAuthHeaders()
  1385→            }
  1386→        );
  1387→
  1388→        if (response.ok) {
  1389→            const data = await response.json();
  1390→            // Backend returns hoursRemaining (not remainingMinutes)
  1391→            const hours = (data.hoursRemaining || 0).toFixed(1);
  1392→            ui.creditBadge.textContent = `${hours}h`;
  1393→            // Low warning when less than 1 hour remaining
  1394→            ui.creditBadge.className = data.hoursRemaining < 1 ? 'credit-badge low' : 'credit-badge ok';
