    padding: 6px 8px;
    background: var(--bg-panel);
    border: 1px solid var(--glass-border);
    border-radius: 4px;
    color: #e1e1e1;
    font-size: 10px;
    resize: none;
}

.music-custom-prompt:focus {
    outline: none;
    border-color: var(--accent-primary);
}

/* Music Options */
.music-options {
    margin-bottom: 10px;
}

/* Generate Actions */
.music-generate-actions {
    display: flex;
    gap: 8px;
}

.music-generate-actions .btn {
    flex: 1;
}

/* Identify Result */
.music-identify-result {
    margin-top: 10px;
    padding: 10px;
    background: var(--bg-panel);
    border-radius: 4px;
    font-size: 10px;
    display: none;
}

.music-identify-result.visible {
    display: block;
}

.identify-song-title {
    font-weight: 600;
    color: #e1e1e1;
    margin-bottom: 4px;
}

.identify-song-artist {
    color: var(--accent-primary);
    margin-bottom: 4px;
}

.identify-song-album,
.identify-song-genres {
    color: var(--text-muted);
    font-size: 9px;
}

/* Music Library */
.music-library-list {
    max-height: 200px;
    overflow-y: auto;
}

.music-library-empty {
    text-align: center;
    color: #666;
    padding: 20px;
    font-size: 10px;
}

.music-library-item {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 8px;
    background: var(--bg-panel);
    border-radius: 4px;
    margin-bottom: 4px;
    cursor: pointer;
    transition: background 0.2s;
}

.music-library-item:hover {
    background: #333;
}

.music-library-item-info {
    flex: 1;
    min-width: 0;
}

.music-library-item-title {
    font-size: 10px;
    color: #e1e1e1;
    font-weight: 500;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.music-library-item-meta {
    font-size: 9px;
    color: var(--text-muted);
    display: flex;
    gap: 8px;
}

.music-library-item-actions {
    display: flex;
    gap: 4px;
}

.music-library-item-actions .btn {
    padding: 4px 6px;
    font-size: 9px;
}

/* Music Status */
.music-status {
    font-size: 10px;
    padding: 4px 0;
    min-height: 18px;
}

.music-status-info {
    color: var(--accent-primary);
}

.music-status-success {
    color: #28a745;
}

.music-status-error {
    color: #dc3545;
}

/* Music Progress */
.music-progress {
    margin: 10px 0;
}

.music-progress.hidden {
    display: none;
}

.music-progress-bar-container {
    height: 4px;
