}

.music-progress-text {
    font-size: 10px;
    color: var(--text-muted);
    text-align: center;
}

/* Music Variations Panel */
.music-variations-panel {
    margin: 10px 0;
    padding: 10px;
    background: var(--bg-panel);
    border-radius: 4px;
}

.music-variations-panel.hidden {
    display: none;
}

.music-variations-panel>label {
    display: block;
    font-size: 10px;
    color: var(--text-muted);
    margin-bottom: 8px;
}

.music-variations-list {
    display: flex;
    flex-direction: column;
    gap: 6px;
}

.music-variation-card {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 8px;
    background: var(--bg-card);
    border: 2px solid transparent;
    border-radius: 4px;
    cursor: pointer;
    transition: all 0.2s;
