    border-radius: 4px;
}

.music-preview.hidden {
    display: none;
}

.music-preview-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 8px;
}

.music-preview-header span {
    font-size: 10px;
    color: #aaa;
}

#music-audio-player {
    width: 100%;
    height: 30px;
    margin-bottom: 8px;
}

.music-preview-actions {
    display: flex;
    gap: 6px;
}

.music-preview-actions .btn {
    flex: 1;
}

/* Music Align Modal */
.music-align-modal,
.music-timeline-modal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.7);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1000;
}

.music-align-modal.hidden,
.music-timeline-modal.hidden {
    display: none;
}

.music-align-content,
.music-timeline-content {
    background: var(--bg-card);
    border-radius: 8px;
    width: 90%;
    max-width: 280px;
    padding: 16px;
    border: 1px solid var(--glass-border);
}

.music-align-header,
.music-timeline-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 12px;
}

.music-align-header span,
.music-timeline-header span {
    font-size: 12px;
    font-weight: 600;
    color: #e1e1e1;
}

.music-align-info,
.music-timeline-info {
    font-size: 10px;
    color: var(--text-muted);
    margin-bottom: 12px;
    padding: 8px;
    background: var(--bg-panel);
    border-radius: 4px;
}

.music-align-info .info-row {
    display: flex;
    justify-content: space-between;
    margin-bottom: 4px;
}

.music-align-info .info-label {
    color: #666;
}

.music-align-info .info-value {
    color: #e1e1e1;
}

.music-align-actions,
.music-timeline-actions {
    margin-top: 12px;
}

.music-align-actions .btn,
.music-timeline-actions .btn {
    width: 100%;
}

/* Hidden utility */
.hidden {
    display: none !important;
}

/* Collapsed utility */
.collapsed {
    display: none !important;
}

/* Scrollbar */
::-webkit-scrollbar {
    width: 6px;
}

::-webkit-scrollbar-track {
    background: var(--bg-panel);
}

::-webkit-scrollbar-thumb {
    background: var(--glass-border);
    border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
    background: #555;
}

/* Referral Promo Footer */
.referral-promo {
    position: fixed;
    bottom: 0;
    left: 0;
    right: 0;
    background: linear-gradient(135deg, rgba(0, 212, 255, 0.1), rgba(138, 43, 226, 0.1));
    border-top: 1px solid rgba(0, 212, 255, 0.2);
    padding: 8px 12px;
    z-index: 100;
}

.referral-promo-content {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 8px;
    font-size: 11px;
}

.referral-icon {
    font-size: 14px;
}

.referral-text {
    color: var(--text-muted);
}

.referral-text strong {
    color: #00D4FF;
}

.referral-link {
    color: #00D4FF;
    text-decoration: none;
    font-weight: 500;
    padding: 2px 8px;
    border-radius: 10px;
    background: rgba(0, 212, 255, 0.1);
    transition: all 0.2s var(--ease-smooth);
}

.referral-link:hover {
    background: rgba(0, 212, 255, 0.2);
    transform: translateY(-1px);
}

