   413→/**
   414→ * Debounce function calls
   415→ */
   416→function debounce(func, wait) {
   417→    let timeout;
   418→    return function executedFunction(...args) {
   419→        const later = () => {
   420→            clearTimeout(timeout);
   421→            func(...args);
   422→        };
   423→        clearTimeout(timeout);
   424→        timeout = setTimeout(later, wait);
   425→    };
   426→}
