   750→
   751→                if (!result.success) {
   752→                    showLoginError(result.error || 'Activation failed');
   753→                    return;
   754→                }
   755→
   756→                saveSettings({ customerId: result.customerId });
   757→                loginModal?.classList.add('hidden');
