     1→<!DOCTYPE html>
     2→<html>
     3→<head>
     4→  <meta charset="utf-8">
     5→  <title>SPLICE</title>
     6→  <style>
     7→    body {
     8→      font-family: Adobe Clean, -apple-system, BlinkMacSystemFont, sans-serif;
     9→      background: #323232;
    10→      color: #e1e1e1;
    11→      margin: 0;
    12→      padding: 8px;
    13→      height: 100vh;
    14→      box-sizing: border-box;
    15→      overflow-y: auto;
    16→      overflow-x: hidden;
    17→    }
    18→
    19→    /* Header */
    20→    .header {
    21→      display: flex;
    22→      justify-content: space-between;
    23→      align-items: center;
    24→      margin-bottom: 8px;
    25→    }
    26→    .header h1 {
    27→      font-size: 14px;
    28→      font-weight: 600;
    29→      margin: 0;
    30→    }
    31→    .header-actions {
    32→      display: flex;
    33→      gap: 4px;
    34→    }
    35→    .icon-btn {
    36→      background: transparent;
    37→      border: 1px solid #555;
    38→      color: #aaa;
    39→      width: 24px;
    40→      height: 24px;
    41→      border-radius: 4px;
    42→      cursor: pointer;
    43→      font-size: 12px;
    44→      display: flex;
    45→      align-items: center;
    46→      justify-content: center;
    47→      padding: 0;
    48→      margin: 0;
    49→    }
    50→    .icon-btn:hover {
    51→      background: #444;
    52→      color: #fff;
    53→    }
    54→
    55→    /* Credit Badge */
    56→    .credit-badge {
    57→      display: none;
    58→      align-items: center;
    59→      font-size: 10px;
    60→      font-weight: 500;
    61→      padding: 3px 8px;
    62→      border-radius: 10px;
    63→      background: #1e1e1e;
    64→      border: 1px solid #444;
    65→      color: #aaa;
    66→      margin-right: 8px;
    67→    }
    68→    .credit-badge.ok {
    69→      color: #4a9eff;
    70→      border-color: #4a9eff33;
    71→    }
    72→    .credit-badge.low {
    73→      color: #dc3545;
    74→      border-color: #dc354533;
    75→      animation: pulse-low 2s infinite;
    76→    }
    77→    .credit-badge.error {
    78→      color: #ff9800;
    79→      border-color: #ff980033;
    80→      cursor: pointer;
    81→    }
    82→    .credit-badge.error:hover {
    83→      background: #332200;
    84→      border-color: #ff9800;
    85→    }
    86→    .credit-badge.login {
    87→      color: #888;
    88→      border-color: #44444433;
    89→      cursor: pointer;
    90→    }
    91→    .credit-badge.login:hover {
    92→      background: #333;
    93→      border-color: #666;
    94→    }
    95→    @keyframes pulse-low {
    96→      0%, 100% { opacity: 1; }
    97→      50% { opacity: 0.6; }
    98→    }
    99→
   100→    /* Collapsed state for collapsible sections */
   101→    .collapsed {
   102→      display: none !important;
   103→    }
   104→
   105→    /* J-Cut slider styles */
   106→    .jcut-options input[type="range"] {
   107→      height: 4px;
   108→      border-radius: 2px;
   109→      background: #444;
   110→    }
   111→
   112→    /* Status bar */
   113→    #status {
   114→      padding: 6px 10px;
   115→      background: #1e1e1e;
   116→      border-radius: 4px;
   117→      font-size: 11px;
   118→      margin-bottom: 10px;
   119→    }
   120→
   121→    /* Action Cards Container */
   122→    .action-cards {
   123→      display: flex;
   124→      gap: 8px;
   125→      margin-bottom: 10px;
   126→    }
   127→
   128→    /* Action Card */
   129→    .action-card {
   130→      flex: 1;
   131→      background: #2a2a2a;
   132→      border-radius: 6px;
   133→      padding: 12px;
   134→      border: 1px solid #444;
   135→    }
   136→    .action-card h2 {
   137→      font-size: 11px;
   138→      font-weight: 600;
   139→      margin: 0 0 10px 0;
   140→      color: #ccc;
   141→      text-transform: uppercase;
   142→      letter-spacing: 0.5px;
   143→    }
   144→
   145→    /* GO Button */
   146→    .go-btn {
   147→      width: 100%;
   148→      padding: 14px 12px;
   149→      font-size: 13px;
   150→      font-weight: 700;
   151→      background: linear-gradient(135deg, #4a9eff, #2d7dd2);
   152→      border: none;
   153→      border-radius: 6px;
   154→      color: white;
   155→      cursor: pointer;
   156→      margin-bottom: 8px;
   157→      letter-spacing: 1px;
   158→    }
   159→    .go-btn:hover {
   160→      background: linear-gradient(135deg, #5aa8ff, #3d8de2);
   161→    }
   162→    .go-btn:disabled {
   163→      opacity: 0.5;
   164→      cursor: not-allowed;
   165→    }
   166→    .go-btn.takes-btn {
   167→      background: linear-gradient(135deg, #28a745, #1e7e34);
   168→    }
   169→    .go-btn.takes-btn:hover {
   170→      background: linear-gradient(135deg, #34ce57, #28a745);
   171→    }
   172→
   173→    /* Options Toggle */
   174→    .options-toggle {
   175→      background: transparent;
   176→      border: none;
   177→      color: #888;
   178→      font-size: 10px;
   179→      cursor: pointer;
   180→      padding: 4px 0;
   181→      width: 100%;
   182→      text-align: left;
   183→      margin: 0;
   184→    }
   185→    .options-toggle:hover {
   186→      color: #aaa;
   187→    }
   188→    .options-toggle::before {
   189→      content: '+';
   190→      margin-right: 4px;
   191→      font-weight: bold;
   192→    }
   193→    .options-toggle.expanded::before {
   194→      content: '-';
   195→    }
   196→
   197→    /* Options Panel (collapsible) */
   198→    .options-panel {
   199→      margin-top: 8px;
   200→      padding-top: 8px;
   201→      border-top: 1px solid #444;
   202→    }
   203→    .options-panel.collapsed {
   204→      display: none;
   205→    }
   206→
   207→    /* Slider */
   208→    .slider-container {
   209→      margin-bottom: 10px;
   210→    }
   211→    .slider-container sp-slider {
   212→      width: 100%;
   213→    }
   214→    .slider-labels {
   215→      display: flex;
   216→      justify-content: space-between;
   217→      font-size: 9px;
   218→      color: #666;
   219→      margin-top: 2px;
   220→    }
   221→
   222→    /* Audio Source */
   223→    .audio-source-group {
   224→      margin-bottom: 8px;
   225→    }
   226→    .audio-source-group sp-label {
   227→      display: block;
   228→      font-size: 10px;
   229→      color: #888;
   230→      margin-bottom: 4px;
   231→    }
   232→    .audio-source-group sp-checkbox {
   233→      display: block;
   234→      margin: 4px 0;
   235→      font-size: 11px;
   236→    }
   237→    .tier-badge {
   238→      font-size: 8px;
   239→      background: #4a9eff;
   240→      color: white;
   241→      padding: 1px 4px;
   242→      border-radius: 2px;
   243→      margin-left: 4px;
   244→      vertical-align: middle;
   245→    }
   246→
   247→    /* Results Area */
   248→    .results-area {
   249→      background: #1e1e1e;
   250→      border-radius: 6px;
   251→      padding: 12px;
   252→      min-height: 60px;
   253→    }
   254→    .results-area.hidden {
   255→      display: none;
   256→    }
   257→    .results-header {
   258→      display: flex;
   259→      justify-content: space-between;
   260→      align-items: center;
   261→      margin-bottom: 8px;
   262→    }
   263→    .results-header h3 {
   264→      font-size: 11px;
   265→      font-weight: 600;
   266→      margin: 0;
   267→      color: #aaa;
   268→      text-transform: uppercase;
   269→    }
   270→    .undo-btn {
   271→      background: #dc3545;
   272→      color: white;
   273→      border: none;
   274→      padding: 4px 10px;
   275→      border-radius: 3px;
   276→      font-size: 10px;
   277→      cursor: pointer;
   278→    }
   279→    .undo-btn:hover {
   280→      background: #c82333;
   281→    }
   282→
   283→    /* Progress */
   284→    .progress-container {
   285→      text-align: center;
   286→      padding: 16px 0;
   287→    }
   288→    .progress-container sp-progressbar {
   289→      width: 100%;
   290→      margin-bottom: 8px;
   291→    }
   292→    .progress-text {
   293→      font-size: 11px;
   294→      color: #888;
   295→    }
   296→
   297→    /* Results Content */
   298→    .results-content {
   299→      font-size: 11px;
   300→    }
   301→    .results-stat {
   302→      display: flex;
   303→      justify-content: space-between;
   304→      padding: 4px 0;
   305→      border-bottom: 1px solid #333;
   306→    }
   307→    .results-stat:last-child {
   308→      border-bottom: none;
   309→    }
   310→    .results-stat-label {
   311→      color: #888;
   312→    }
   313→    .results-stat-value {
   314→      font-weight: 600;
   315→      color: #4a9eff;
   316→    }
   317→
   318→    /* Preview Section */
   319→    .preview-section {
   320→      display: none;
   321→    }
   322→    .preview-header {
   323→      display: flex;
   324→      justify-content: space-between;
   325→      align-items: center;
   326→      margin-bottom: 10px;
   327→    }
   328→    .preview-header h3 {
   329→      font-size: 11px;
   330→      font-weight: 600;
   331→      margin: 0;
   332→      color: #aaa;
   333→      text-transform: uppercase;
   334→    }
   335→    .preview-header-actions {
   336→      display: flex;
   337→      gap: 4px;
   338→    }
   339→    .preview-summary {
   340→      font-size: 10px;
   341→      color: #888;
   342→      margin-bottom: 10px;
   343→      padding: 8px;
   344→      background: #252525;
   345→      border-radius: 4px;
   346→    }
   347→    .preview-summary strong {
   348→      color: #4a9eff;
   349→    }
   350→    .preview-list {
   351→      max-height: 180px;
   352→      overflow-y: auto;
   353→      margin-bottom: 10px;
   354→      contain: layout style paint;
   355→      will-change: scroll-position;
   356→    }
   357→    .preview-item {
   358→      display: flex;
   359→      align-items: center;
   360→      padding: 8px 10px;
   361→      min-height: 44px;
   362→      margin-bottom: 4px;
   363→      background: #2a2a2a;
   364→      border-radius: 4px;
   365→      border-left: 3px solid #4a9eff;
   366→      cursor: pointer;
   367→      transition: background-color 0.15s ease, border-left-color 0.15s ease;
   368→      contain: layout style;
   369→      box-sizing: border-box;
   370→    }
   371→    .preview-item:hover {
   372→      background: #333;
   373→    }
   374→    .preview-item:focus-within {
   375→      outline: 2px solid #4a9eff;
   376→      outline-offset: -2px;
   377→    }
   378→    .preview-item.excluded {
   379→      opacity: 0.65;
   380→      border-left-color: #666;
   381→    }
   382→    .preview-item-check {
   383→      min-width: 20px;
   384→      min-height: 20px;
   385→      margin-right: 10px;
   386→      accent-color: #4a9eff;
   387→      cursor: pointer;
   388→    }
   389→    .preview-item-info {
   390→      flex: 1;
   391→      min-width: 0;
   392→      overflow: hidden;
   393→    }
   394→    .preview-item-time {
   395→      font-size: 11px;
   396→      font-weight: 500;
   397→      color: #e1e1e1;
   398→      white-space: nowrap;
   399→      overflow: hidden;
   400→      text-overflow: ellipsis;
   401→    }
   402→    .preview-item-duration {
   403→      font-size: 9px;
   404→      color: #999;
   405→      margin-top: 2px;
   406→    }
   407→    .preview-item-seek {
   408→      background: transparent;
   409→      border: 1px solid #555;
   410→      color: #888;
   411→      min-width: 32px;
   412→      min-height: 32px;
   413→      border-radius: 4px;
   414→      cursor: pointer;
   415→      font-size: 12px;
   416→      display: flex;
   417→      align-items: center;
   418→      justify-content: center;
   419→      flex-shrink: 0;
   420→    }
   421→    .preview-item-seek:hover {
   422→      background: #444;
   423→      color: #fff;
   424→    }
   425→    .preview-item-seek:focus-visible {
   426→      outline: 2px solid #4a9eff;
   427→      outline-offset: 2px;
   428→    }
   429→    .preview-item-seek.loading {
   430→      pointer-events: none;
   431→      opacity: 0.6;
   432→    }
   433→    .preview-actions {
   434→      display: flex;
   435→      gap: 8px;
   436→      margin-top: 10px;
   437→    }
   438→    .preview-actions button {
   439→      flex: 1;
   440→      padding: 12px 10px;
   441→      min-height: 44px;
   442→      font-size: 11px;
   443→      font-weight: 600;
   444→      border-radius: 4px;
   445→      cursor: pointer;
   446→    }
   447→    .apply-btn {
   448→      background: linear-gradient(135deg, #28a745, #1e7e34);
   449→      color: white;
   450→      border: none;
   451→    }
   452→    .apply-btn:hover {
   453→      background: linear-gradient(135deg, #34ce57, #28a745);
   454→    }
   455→    .apply-btn:disabled {
   456→      opacity: 0.5;
   457→      cursor: not-allowed;
   458→    }
   459→    .apply-btn:focus-visible {
   460→      outline: 2px solid #28a745;
   461→      outline-offset: 2px;
   462→    }
   463→    .cancel-btn {
   464→      background: transparent;
   465→      color: #888;
   466→      border: 1px solid #555;
   467→    }
   468→    .cancel-btn:hover {
   469→      background: #333;
   470→      color: #e1e1e1;
   471→    }
   472→    .cancel-btn:focus-visible {
   473→      outline: 2px solid #888;
   474→      outline-offset: 2px;
   475→    }
   476→    .select-all-row {
   477→      display: flex;
   478→      justify-content: space-between;
   479→      align-items: center;
   480→      padding: 6px 0;
   481→      margin-bottom: 8px;
   482→      border-bottom: 1px solid #333;
   483→    }
   484→    .select-all-row label {
   485→      font-size: 10px;
   486→      color: #888;
   487→      display: flex;
   488→      align-items: center;
   489→      cursor: pointer;
   490→    }
   491→    .select-all-row input {
   492→      margin-right: 6px;
   493→      min-width: 16px;
   494→      min-height: 16px;
   495→      accent-color: #4a9eff;
   496→    }
   497→    .preview-filters {
   498→      display: flex;
   499→      gap: 4px;
   500→      flex-wrap: wrap;
   501→      margin-bottom: 8px;
   502→    }
   503→    .filter-btn {
   504→      background: #1e1e1e;
   505→      border: 1px solid #444;
   506→      color: #888;
   507→      padding: 4px 8px;
   508→      font-size: 9px;
   509→      border-radius: 3px;
   510→      cursor: pointer;
   511→    }
   512→    .filter-btn:hover {
   513→      background: #333;
   514→      color: #e1e1e1;
   515→    }
   516→    .filter-btn.active {
   517→      background: #4a9eff22;
   518→      border-color: #4a9eff;
   519→      color: #4a9eff;
   520→    }
   521→
   522→    /* Legend dots */
   523→    .legend-item {
   524→      display: inline-flex;
   525→      align-items: center;
   526→      margin-right: 12px;
   527→    }
   528→    .legend-dot {
   529→      display: inline-block;
   530→      width: 8px;
   531→      height: 8px;
   532→      border-radius: 50%;
   533→      margin-right: 4px;
   534→    }
   535→    .legend-dot.silence {
   536→      background: #dc3545;
   537→    }
   538→    .legend-dot.take {
   539→      background: #4a9eff;
   540→    }
   541→
   542→    /* Colored markers for preview items */
   543→    .preview-item.silence-marker {
   544→      border-left-color: #dc3545;
   545→    }
   546→    .preview-item.take-marker {
   547→      border-left-color: #4a9eff;
   548→    }
   549→    .preview-item.protected-silence {
   550→      border-left-color: #ffc107;
   551→      opacity: 0.6;
   552→    }
   553→    .preview-item.protected-silence::after {
