   556→      color: #ffc107;
   557→      margin-left: 8px;
   558→    }
   559→
   560→    /* Repetition Highlighting Colors */
   561→    .repetition-phrase {
   562→      display: inline;
   563→      padding: 2px 4px;
   564→      border-radius: 3px;
   565→      margin: 1px;
   566→      font-size: 10px;
   567→      cursor: pointer;
