   637→    }
   638→    .take-time {
   639→      color: #888;
   640→      font-size: 9px;
   641→      margin-top: 2px;
   642→    }
   643→    .take-text {
   644→      color: #aaa;
   645→      font-size: 10px;
   646→      margin-top: 4px;
   647→    }
   648→    .take-actions {
   649→      display: flex;
   650→      gap: 6px;
   651→      margin-top: 10px;
   652→    }
   653→    .take-actions button {
   654→      flex: 1;
   655→      padding: 8px;
   656→      font-size: 10px;
   657→      border-radius: 4px;
   658→    }
   659→    .keep-best-btn {
