   676→    }
   677→
   678→    /* Empty State */
   679→    .empty-state {
   680→      text-align: center;
   681→      color: #666;
   682→      font-size: 11px;
   683→      padding: 20px 0;
   684→    }
   685→
   686→    /* Razor Section (Advanced - hidden by default) */
   687→    .advanced-section {
   688→      margin-top: 10px;
   689→      padding: 10px;
   690→      background: #252525;
   691→      border-radius: 4px;
   692→      border: 1px dashed #444;
   693→    }
   694→    .advanced-section.hidden {
   695→      display: none;
   696→    }
   697→    .advanced-section h4 {
