   729→/**
   730→ * Apply a preset to current settings
   731→ * @param {string} presetName - Name of the preset to apply
   732→ * @returns {Object} The applied settings
   733→ */
   734→function applyPreset(presetName) {
   735→  const preset = PRESETS[presetName];
   736→
   737→  if (!preset) {
   738→    console.warn(`[SPLICE] Unknown preset: ${presetName}`);
   739→    return getSettings();
   740→  }
   741→
   742→  // Custom preset uses current settings
   743→  if (presetName === 'custom' || !preset.settings) {
   744→    saveSettings({ activePreset: 'custom' });
   745→    return getSettings();
   746→  }
   747→
   748→  // Apply preset settings
   749→  const newSettings = {
   750→    ...preset.settings,
   751→    activePreset: presetName
   752→  };
   753→
   754→  saveSettings(newSettings);
   755→  console.log(`[SPLICE] Applied preset: ${preset.name}`);
   756→
   757→  return getSettings();
   758→}
