   769→/**
   770→ * Check if current settings match a preset
   771→ * @param {string} presetName - Preset to compare against
   772→ * @returns {boolean} True if settings match the preset
   773→ */
   774→function settingsMatchPreset(presetName) {
   775→  const preset = PRESETS[presetName];
   776→  if (!preset || !preset.settings) return presetName === 'custom';
   777→
   778→  const current = getSettings();
   779→
   780→  // Compare relevant settings
   781→  return Object.keys(preset.settings).every(key => {
   782→    if (key === 'activePreset') return true;
   783→    return current[key] === preset.settings[key];
   784→  });
   785→}
