   789→/**
   790→ * Load settings from localStorage
   791→ */
   792→function loadSettings() {
   793→  try {
   794→    const saved = localStorage.getItem('spliceSettings');
   795→    if (saved) {
   796→      currentSettings = { ...DEFAULT_SETTINGS, ...JSON.parse(saved) };
   797→    }
   798→  } catch (e) {
   799→    console.warn('[SPLICE] Could not load settings:', e);
   800→    currentSettings = { ...DEFAULT_SETTINGS };
   801→  }
   802→  return currentSettings;
   803→}
