   805→/**
   806→ * Save settings to localStorage
   807→ */
   808→function saveSettings(settings) {
   809→  try {
   810→    currentSettings = { ...currentSettings, ...settings };
   811→    localStorage.setItem('spliceSettings', JSON.stringify(currentSettings));
   812→  } catch (e) {
   813→    console.warn('[SPLICE] Could not save settings:', e);
   814→  }
   815→}
