   825→ * Reset settings to defaults
   826→ */
   827→function resetSettings() {
   828→  currentSettings = { ...DEFAULT_SETTINGS };
   829→  try {
   830→    localStorage.removeItem('spliceSettings');
   831→  } catch (e) {
   832→    console.warn('[SPLICE] Could not clear settings:', e);
   833→  }
