  1169→}
  1170→
  1171→/**
  1172→ * Read a file from the media folder
  1173→ * @param {string} filename - Name of the file to read
  1174→ * @returns {Promise<ArrayBuffer|null>} File contents or null
  1175→ */
  1176→async function readMediaFile(filename) {
  1177→  const folder = await getMediaFolder();
  1178→  if (!folder) {
  1179→    console.warn('[SPLICE] No media folder configured');
  1180→    return null;
  1181→  }
  1182→
  1183→  try {
  1184→    const file = await folder.getEntry(filename);
  1185→    if (!file) {
  1186→      console.warn(`[SPLICE] File not found in media folder: ${filename}`);
  1187→      return null;
  1188→    }
  1189→
  1190→    const contents = await file.read({ format: uxpFs.formats.binary });
  1191→    return contents;
  1192→  } catch (err) {
  1193→    console.error(`[SPLICE] Error reading file ${filename}:`, err);
  1194→    return null;
  1195→  }
  1196→}
