  1202→/**
  1203→ * Validate license key format: SPLICE-XXXX-XXXX-XXXX
  1204→ * @param {string} key - License key to validate
  1205→ * @returns {boolean} True if format is valid
  1206→ */
  1207→function isValidLicenseKeyFormat(key) {
  1208→  if (!key || typeof key !== 'string') return false;
  1209→  const normalized = key.toUpperCase().trim();
  1210→  const pattern = /^SPLICE-[A-Z2-9]{4}-[A-Z2-9]{4}-[A-Z2-9]{4}$/;
  1211→  return pattern.test(normalized);
  1212→}
