  1255→  }
  1256→
  1257→  // Activate license key
  1258→  if (saveLoginBtn) {
  1259→    saveLoginBtn.addEventListener('click', async () => {
  1260→      const licenseKey = licenseKeyInput?.value?.trim()?.toUpperCase();
  1261→
  1262→      if (!licenseKey) {
  1263→        showLoginError('Please enter your license key');
  1264→        return;
  1265→      }
  1266→
  1267→      if (!isValidLicenseKeyFormat(licenseKey)) {
  1268→        showLoginError('Invalid format. Expected: SPLICE-XXXX-XXXX-XXXX');
  1269→        return;
  1270→      }
  1271→
  1272→      // Show loading state
  1273→      saveLoginBtn.disabled = true;
  1274→      saveLoginBtn.textContent = 'Activating...';
  1275→
  1276→      try {
  1277→        // Call license activation API (uses fetchWithTimeout from config.js)
  1278→        const response = await fetchWithTimeout(`${getBackendUrl()}/license/activate`, {
  1279→          method: 'POST',
  1280→          headers: { 'Content-Type': 'application/json' },
  1281→          body: JSON.stringify({ key: licenseKey })
  1282→        }, FETCH_TIMEOUT_DEFAULT);
  1283→
  1284→        const result = await response.json();
  1285→
  1286→        if (!result.success) {
  1287→          showLoginError(result.error || 'Activation failed');
  1288→          return;
  1289→        }
  1290→
  1291→        // Save customer ID from activation response
  1292→        saveSettings({ customerId: result.customerId });
  1293→
  1294→        // Hide modal
  1295→        loginModal?.classList.add('hidden');
  1296→
  1297→        // Refresh credits display
  1298→        if (typeof refreshCredits === 'function') {
  1299→          await refreshCredits();
  1300→        }
  1301→
  1302→        if (typeof setStatus === 'function') {
  1303→          setStatus(`License activated! ${result.tierName} tier - ${result.hoursRemaining.toFixed(1)}h remaining`);
  1304→        }
  1305→      } catch (err) {
  1306→        console.error('[SPLICE] License activation error:', err);
  1307→        showLoginError('Connection error. Check server is running.');
  1308→      } finally {
  1309→        saveLoginBtn.disabled = false;
  1310→        saveLoginBtn.textContent = 'Activate';
  1311→      }
  1312→    });
  1313→  }
  1314→
  1315→  // Email-based license lookup
  1316→  const lookupLicenseBtn = document.getElementById('lookupLicenseBtn');
  1317→  const lookupEmailInput = document.getElementById('lookupEmailInput');
  1318→  const lookupResult = document.getElementById('lookupResult');
