  1325→      if (!email) {
  1326→        showLoginError('Please enter your email address');
  1327→        return;
  1328→      }
  1329→
  1330→      // Basic email validation
  1331→      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1332→      if (!emailRegex.test(email)) {
  1333→        showLoginError('Please enter a valid email address');
  1334→        return;
  1335→      }
  1336→
  1337→      // Hide previous results/errors
  1338→      if (loginError) loginError.style.display = 'none';
  1339→      if (lookupResult) lookupResult.style.display = 'none';
  1340→
  1341→      // Show loading state
