   491→/**
   492→ * Fetch zoom points from backend
   493→ * @param {Object} transcript - Transcript data
   494→ * @returns {Promise<Array>} Zoom points
   495→ */
   496→async function fetchZoomPoints(transcript) {
   497→  const settings = getZoomSettings();
   498→  if (!settings.enabled) return [];
   499→
   500→  try {
   501→    setStatus('Generating zoom points...');
   502→
   503→    const response = await fetchWithTimeout(
   504→      `${getBackendUrl()}/zoom`,
   505→      {
   506→        method: 'POST',
   507→        headers: {
   508→          'Content-Type': 'application/json',
   509→          ...getAuthHeaders()
   510→        },
   511→        body: JSON.stringify({ transcript, settings })
   512→      },
   513→      30000
   514→    );
   515→
   516→    if (!response.ok) {
   517→      const errData = parseErrorResponse(await response.text());
   518→      throw new Error(errData.error || 'Zoom generation failed');
   519→    }
   520→
   521→    const data = await response.json();
   522→    currentZoomPoints = data.zoomPoints || [];
   523→    displayZoomResults(currentZoomPoints);
   524→    return currentZoomPoints;
   525→
   526→  } catch (err) {
   527→    console.error('[SPLICE] Zoom fetch error:', err);
   528→    setStatus(`Zoom error: ${err.message}`);
   529→    return [];
   530→  }
   531→}
