   533→/**
   534→ * Display zoom results in the UI
   535→ * @param {Array} zoomPoints - Zoom points
   536→ */
   537→function displayZoomResults(zoomPoints) {
   538→  if (!ui.zoomResults) return;
   539→
   540→  if (zoomPoints.length === 0) {
   541→    ui.zoomResults.style.display = 'none';
   542→    return;
   543→  }
   544→
   545→  ui.zoomResults.style.display = 'block';
   546→  if (ui.zoomCount) ui.zoomCount.textContent = zoomPoints.length;
   547→
   548→  if (ui.zoomList) {
   549→    ui.zoomList.innerHTML = zoomPoints.map((zoom, i) => `
   550→      <div class="preview-item" data-zoom-index="${i}">
   551→        <div class="preview-item-info">
   552→          <div class="preview-item-time">${formatTime(zoom.startTime)} - ${zoom.reason}</div>
   553→          <div class="preview-item-duration">Scale: ${zoom.scale}%, Duration: ${zoom.duration}s</div>
   554→        </div>
   555→      </div>
   556→    `).join('');
   557→  }
   558→}
