   560→/**
   561→ * Apply zoom effects to timeline (via adjustment layer)
   562→ */
   563→async function applyZoomsToTimeline() {
   564→  if (currentZoomPoints.length === 0) {
   565→    setStatus('No zoom points to apply');
   566→    return;
   567→  }
   568→
   569→  try {
   570→    setStatus('Applying zoom effects...');
   571→
   572→    // Use builder to apply zooms
   573→    if (typeof window.spliceBuilder?.applyZoomKeyframes === 'function') {
   574→      const result = await window.spliceBuilder.applyZoomKeyframes(currentZoomPoints);
   575→      if (result.success) {
   576→        setStatus(`Applied ${result.count} zoom effects`);
   577→      } else {
   578→        setStatus(`Zoom error: ${result.error}`);
   579→      }
   580→    } else {
   581→      // Fallback: just log for now
   582→      console.log('[SPLICE] Zoom keyframes would be applied:', currentZoomPoints);
   583→      setStatus(`${currentZoomPoints.length} zoom points ready (builder integration pending)`);
   584→    }
   585→
   586→  } catch (err) {
   587→    console.error('[SPLICE] Apply zooms error:', err);
   588→    setStatus(`Error: ${err.message}`);
   589→  }
   590→}
