   632→// ============================================================================
   633→// REPETITION HIGHLIGHTING UI (Feature 8)
   634→// ============================================================================
   635→const REPETITION_COLORS = [
   636→  { name: 'Red', class: 'repetition-color-1', hex: '#ff6b6b' },
   637→  { name: 'Teal', class: 'repetition-color-2', hex: '#4ecdc4' },
   638→  { name: 'Yellow', class: 'repetition-color-3', hex: '#ffe66d' },
   639→  { name: 'Mint', class: 'repetition-color-4', hex: '#95e1d3' },
   640→  { name: 'Purple', class: 'repetition-color-5', hex: '#9b59b6' },
   641→  { name: 'Gold', class: 'repetition-color-6', hex: '#f1c40f' }
   642→];
   643→
   644→let currentRepetitions = [];
   645→let selectedRepetitions = new Set();
   646→
   647→/**
   648→ * Render repetition preview with color-coded phrases
   649→ * @param {Object} data - Repetition detection response
   650→ */
   651→function renderRepetitionPreview(data) {
   652→  const container = document.getElementById('repetitionResults');
   653→  const preview = document.getElementById('repetitionPreview');
   654→  const legend = document.getElementById('repetitionLegend');
   655→  const colorsDiv = document.getElementById('repetitionColors');
   656→  const countEl = document.getElementById('repetitionCount');
   657→  const stutterCountEl = document.getElementById('stutterCount');
   658→  const emptyEl = document.getElementById('repetitionEmpty');
   659→
   660→  if (!container || !preview) return;
   661→
   662→  const { repetitions = [], stutters = [], text = '' } = data;
   663→  currentRepetitions = [...repetitions, ...stutters];
   664→  selectedRepetitions = new Set(currentRepetitions.map((_, i) => i));
   665→
   666→  // Update counts
   667→  if (countEl) countEl.textContent = repetitions.length;
   668→  if (stutterCountEl) stutterCountEl.textContent = stutters.length;
   669→
   670→  // Show/hide container
   671→  container.style.display = currentRepetitions.length > 0 ? 'block' : 'none';
   672→
   673→  if (currentRepetitions.length === 0) {
   674→    if (emptyEl) emptyEl.style.display = 'block';
   675→    if (legend) legend.style.display = 'none';
   676→    return;
   677→  }
   678→
   679→  if (emptyEl) emptyEl.style.display = 'none';
   680→
   681→  // Group repetitions by phrase for color assignment
   682→  const phraseGroups = {};
   683→  repetitions.forEach((rep, i) => {
   684→    const phrase = rep.phrase || rep.originalPhrase || '';
   685→    if (!phraseGroups[phrase]) {
   686→      phraseGroups[phrase] = [];
   687→    }
   688→    phraseGroups[phrase].push({ ...rep, index: i, type: 'repetition' });
   689→  });
   690→
   691→  // Assign colors to phrase groups
   692→  const phraseColors = {};
   693→  let colorIndex = 0;
   694→  Object.keys(phraseGroups).forEach(phrase => {
   695→    phraseColors[phrase] = REPETITION_COLORS[colorIndex % REPETITION_COLORS.length];
   696→    colorIndex++;
   697→  });
   698→
   699→  // Build highlighted text
   700→  let html = '';
   701→
   702→  // Render repetitions grouped by phrase
   703→  Object.entries(phraseGroups).forEach(([phrase, instances]) => {
   704→    const color = phraseColors[phrase];
   705→    html += `<div style="margin-bottom: 8px;">`;
   706→    html += `<span style="font-size: 9px; color: #888;">"${phrase}" (${instances.length}x):</span><br>`;
   707→    instances.forEach((rep, i) => {
   708→      const timeStr = formatTime(rep.start || rep.startTime || 0);
   709→      html += `<span class="repetition-phrase ${color.class} selected" data-index="${rep.index}" title="Click to seek: ${timeStr}">`;
   710→      html += `${rep.phrase || rep.originalPhrase} @ ${timeStr}`;
   711→      html += `</span> `;
   712→    });
   713→    html += `</div>`;
   714→  });
   715→
   716→  // Render stutters
   717→  if (stutters.length > 0) {
   718→    html += `<div style="margin-top: 10px; padding-top: 8px; border-top: 1px solid #444;">`;
   719→    html += `<span style="font-size: 9px; color: #888;">Stutters:</span><br>`;
   720→    stutters.forEach((stutter, i) => {
   721→      const timeStr = formatTime(stutter.start || stutter.startTime || 0);
   722→      html += `<span class="repetition-phrase stutter-word selected" data-index="${repetitions.length + i}" title="Click to seek: ${timeStr}">`;
   723→      html += `"${stutter.word}" @ ${timeStr}`;
   724→      html += `</span> `;
   725→    });
   726→    html += `</div>`;
   727→  }
   728→
   729→  preview.innerHTML = html;
   730→
   731→  // Build legend
   732→  if (legend && colorsDiv && Object.keys(phraseColors).length > 0) {
   733→    legend.style.display = 'block';
   734→    let legendHtml = '';
   735→    Object.entries(phraseColors).forEach(([phrase, color]) => {
   736→      legendHtml += `<span class="color-chip ${color.class}">`;
   737→      legendHtml += `<span class="chip-dot" style="background: ${color.hex}"></span>`;
   738→      legendHtml += `"${phrase.substring(0, 20)}${phrase.length > 20 ? '...' : ''}"`;
   739→      legendHtml += `</span>`;
   740→    });
   741→    colorsDiv.innerHTML = legendHtml;
   742→  }
   743→
   744→  // Add click handlers for seeking
   745→  preview.querySelectorAll('.repetition-phrase').forEach(el => {
   746→    el.addEventListener('click', () => {
   747→      const idx = parseInt(el.dataset.index, 10);
   748→      const rep = currentRepetitions[idx];
   749→      if (rep) {
   750→        // Toggle selection
   751→        el.classList.toggle('selected');
   752→        if (selectedRepetitions.has(idx)) {
   753→          selectedRepetitions.delete(idx);
   754→        } else {
   755→          selectedRepetitions.add(idx);
   756→        }
   757→        // Seek to time (if PPro API available)
   758→        const time = rep.start || rep.startTime || 0;
   759→        seekToTime(time);
   760→      }
   761→    });
   762→  });
   763→}
   764→
   765→/**
   766→ * Seek to time in active sequence
   767→ * @param {number} time - Time in seconds
   768→ */
   769→function seekToTime(time) {
   770→  try {
   771→    const seq = getActiveSequence();
   772→    if (seq && typeof seq.setPlayerPosition === 'function') {
   773→      const { TickTime } = require('premierepro').app.project;
   774→      const tickTime = TickTime.createWithSeconds(time);
   775→      seq.setPlayerPosition(tickTime);
   776→      console.log(`[SPLICE] Seeked to ${time.toFixed(2)}s`);
   777→    }
   778→  } catch (err) {
   779→    console.log('[SPLICE] Seek not available:', err.message);
   780→  }
   781→}
   782→
   783→/**
   784→ * Initialize repetition highlighting UI
   785→ */
   786→function initRepetitionUI() {
   787→  const detectBtn = document.getElementById('detectRepetitionsBtn');
   788→  const removeBtn = document.getElementById('removeRepetitionsBtn');
   789→
   790→  if (detectBtn) {
   791→    detectBtn.addEventListener('click', detectRepetitions);
   792→  }
   793→
   794→  if (removeBtn) {
   795→    removeBtn.addEventListener('click', removeSelectedRepetitions);
   796→  }
   797→
   798→  console.log('[SPLICE] Repetition Highlighting UI initialized');
   799→}
