  2100→    silencesToApply = markerSilences;
  2101→    adjustedCount = markerSilences.length;
  2102→    console.log(`[SPLICE] Using ${adjustedCount} silences from adjusted markers`);
  2103→  } else {
  2104→    // Fallback to original detection data
  2105→    silencesToApply = safeSilences.filter((_, i) => selectedSilenceIndices.has(i));
  2106→    console.log(`[SPLICE] Using ${silencesToApply.length} silences from original detection`);
  2107→  }
  2108→
  2109→  if (silencesToApply.length === 0) {
  2110→    setStatus('No silences to apply');
  2111→    return;
  2112→  }
  2113→
  2114→  // Update global silences for the apply function
  2115→  setCurrentSilences(silencesToApply);
