  2205→/**
  2206→ * Format build errors into user-friendly messages
  2207→ * @param {string} error - Raw error message
  2208→ * @returns {string} User-friendly error message
  2209→ */
  2210→function formatBuildError(error) {
  2211→  if (!error) return 'Unknown error occurred';
  2212→
  2213→  // Map technical errors to user-friendly messages
  2214→  const errorMap = {
  2215→    'Builder not initialized': 'Builder module failed to load. Try restarting the plugin.',
  2216→    'No active project': 'No project is open. Please open or create a project first.',
  2217→    'No media items found': 'No media files in project. Import media before building.',
  2218→    'No detection data': 'Run analysis first (click GO button).',
  2219→    'Failed to generate cut list': 'Server error generating cuts. Check your connection.',
  2220→    'Failed to build sequence': 'Premiere Pro sequence build failed. Try again.',
  2221→    'fetch failed': 'Network error. Check if backend server is running.',
  2222→    'NetworkError': 'Cannot connect to server. Check your internet connection.',
  2223→    'Insufficient credits': 'Out of credits. Upgrade your plan to continue.',
  2224→    'Authentication required': 'Login required. Click the credits badge to log in.'
  2225→  };
  2226→
  2227→  // Check for matching error patterns
  2228→  for (const [pattern, friendly] of Object.entries(errorMap)) {
  2229→    if (error.includes(pattern)) {
  2230→      return friendly;
  2231→    }
  2232→  }
  2233→
  2234→  // Return original if no match (already reasonably clear)
  2235→  return error;
  2236→}
