  2324→
  2325→// 1. ROBUST CONSTANTS (Fixes 'undefined' error)
  2326→// We define these integers explicitly because UXP API versions vary on where they store Enums.
  2327→const ProjectItemType = {
  2328→    CLIP: 1,
  2329→    BIN: 2,
  2330→    ROOT: 3,
  2331→    FILE: 4
  2332→};
  2333→
  2334→/**
  2335→ * Find the first media item in the project bin (recursively)
  2336→ * @param {Object} item - ProjectItem (usually rootItem)
  2337→ * @returns {Promise<Object|null>} First media item found or null
  2338→ */
  2339→async function findFirstMediaItem(item) {
  2340→  if (!item) return null;
  2341→
  2342→  // Fix: Use local Constant instead of app.Constants.ProjectItemType
  2343→  if (item.type === ProjectItemType.CLIP) {
  2344→    return item;
  2345→  }
  2346→
  2347→  // Search Children (Bin or Root)
  2348→  // Note: item.children is a collection, check numItems
  2349→  if (item.children && item.children.numItems > 0) {
  2350→    for (let i = 0; i < item.children.numItems; i++) {
  2351→      const child = item.children[i];
  2352→      const found = await findFirstMediaItem(child);
  2353→      if (found) return found;
  2354→    }
  2355→  }
  2356→
  2357→  return null;
  2358→}
