  2360→/**
  2361→ * Build a sequence from detection data locally
  2362→ * Used for testing via console: await buildSequenceV35(silences)
  2363→ */
  2364→async function buildSequenceV35(silences, takes = []) {
  2365→    if (!window.spliceBuilder) {
  2366→        throw new Error('Builder not initialized. Check if builder.js is loaded.');
  2367→    }
  2368→
  2369→    const app = require('premierepro');
  2370→    const project = await app.Project.getActiveProject();
