   453→                const zoomResponse = await fetchWithTimeout(
   454→                    `${getBackendUrl()}/zoom`,
   455→                    {
   456→                        method: 'POST',
   457→                        headers: getAuthHeaders(),
   458→                        body: JSON.stringify({
   459→                            frequency: settings.zoomFrequency || 'medium',
   460→                            preset: settings.zoomPreset || 'medium',
   461→                            placement: settings.zoomPlacement || 'sentence_start'
   462→                        })
   463→                    }
   464→                );
   465→
   466→                if (zoomResponse.ok) {
   467→                    const zoomData = await zoomResponse.json();
   468→                    currentZooms = zoomData.zoomPoints || [];
   469→                }
   470→            } catch (e) {
   471→                console.warn('[SPLICE] Zoom detection failed:', e);
   472→            }
   473→        }
   474→
   475→        // Store results
   476→        lastDetectionResult = {
   477→            silences: previewSilences,
   478→            takes: previewTakes,
   479→            chapters: currentChapters,
   480→            zooms: currentZooms
   481→        };
   482→
   483→        // Show preview
   484→        hideProgress();
   485→        showCombinedPreview();
   486→        setStatus(`Found ${previewSilences.length} silences, ${previewTakes.length} takes`);
   487→
   488→    } catch (error) {
   489→        console.error('[SPLICE] Detection error:', error);
   490→        setStatus('Detection failed: ' + error.message, true);
   491→        hideProgress();
   492→    } finally {
   493→        isOperationInProgress = false;
   494→        ui.goBtn.disabled = false;
   495→    }
   496→}
   497→
   498→// ============================================================================
   499→// PROGRESS UI
   500→// ============================================================================
   501→function showProgress(message) {
   502→    if (ui.progressContainer) ui.progressContainer.classList.remove('hidden');
   503→    if (ui.resultsEmpty) ui.resultsEmpty.classList.add('hidden');
   504→    if (ui.combinedPreview) ui.combinedPreview.classList.add('hidden');
   505→    updateProgress(0, message);
   506→}
   507→
   508→function updateProgress(percent, message) {
   509→    if (ui.progressBar) ui.progressBar.style.width = percent + '%';
   510→    if (ui.progressText) ui.progressText.textContent = message || '';
   511→}
   512→
   513→function hideProgress() {
   514→    if (ui.progressContainer) ui.progressContainer.classList.add('hidden');
   515→}
   516→
   517→// ============================================================================
   518→// COMBINED PREVIEW
   519→// ============================================================================
   520→function showCombinedPreview() {
   521→    if (!ui.combinedPreview) return;
   522→
   523→    ui.combinedPreview.classList.remove('hidden');
   524→    if (ui.resultsEmpty) ui.resultsEmpty.classList.add('hidden');
   525→
   526→    // Update counts
   527→    if (ui.silenceCount) ui.silenceCount.textContent = previewSilences.length;
   528→    if (ui.takeCount) ui.takeCount.textContent = previewTakes.length;
   529→
   530→    // Select all by default
   531→    selectedSilenceIndices.clear();
   532→    previewSilences.forEach((_, i) => selectedSilenceIndices.add(i));
   533→    updateSelectedCount();
   534→
   535→    // Render preview list
   536→    renderPreviewList();
   537→
   538→    // Show chapter results if available
   539→    if (currentChapters.length > 0 && ui.chapterResults) {
   540→        ui.chapterResults.classList.remove('hidden');
   541→        renderChapterList();
   542→    }
   543→
   544→    // Show zoom results if available
   545→    if (currentZooms.length > 0 && ui.zoomResults) {
   546→        ui.zoomResults.classList.remove('hidden');
   547→    }
   548→}
   549→
   550→function renderPreviewList() {
   551→    if (!ui.previewList) return;
   552→
   553→    const html = [];
   554→
   555→    // Render silences
   556→    previewSilences.forEach((silence, i) => {
   557→        const isSelected = selectedSilenceIndices.has(i);
   558→        const duration = silence.end - silence.start;
   559→        html.push(`
   560→            <div class="preview-item ${isSelected ? '' : 'excluded'}" data-index="${i}" data-type="silence">
   561→                <input type="checkbox" class="preview-item-check" ${isSelected ? 'checked' : ''}>
   562→                <div class="preview-item-info">
   563→                    <div class="preview-item-time">${formatTime(silence.start)} - ${formatTime(silence.end)}</div>
   564→                    <div class="preview-item-duration">${duration.toFixed(2)}s silence</div>
   565→                </div>
   566→            </div>
   567→        `);
   568→    });
   569→
   570→    // Render takes
   571→    previewTakes.forEach((take, i) => {
   572→        html.push(`
   573→            <div class="preview-item take-item" data-index="${i}" data-type="take">
   574→                <div class="preview-item-icon">🎬</div>
   575→                <div class="preview-item-info">
   576→                    <div class="preview-item-time">${formatTime(take.start)} - ${formatTime(take.end)}</div>
   577→                    <div class="preview-item-label">${take.label || 'Take ' + (take.takeNumber || i + 1)}</div>
   578→                </div>
   579→            </div>
   580→        `);
   581→    });
   582→
   583→    ui.previewList.innerHTML = html.join('');
   584→
   585→    // Add click handlers
   586→    ui.previewList.querySelectorAll('.preview-item').forEach(item => {
   587→        item.addEventListener('click', (e) => {
   588→            const index = parseInt(item.dataset.index);
   589→            const type = item.dataset.type;
   590→
   591→            if (e.target.classList.contains('preview-item-check')) {
   592→                // Checkbox clicked
   593→                toggleSilenceSelection(index);
   594→            } else {
   595→                // Item clicked - seek to time
   596→                const data = type === 'silence' ? previewSilences[index] : previewTakes[index];
   597→                seekToTime(data.start);
   598→            }
   599→        });
   600→    });
   601→}
   602→
   603→function toggleSilenceSelection(index) {
   604→    if (selectedSilenceIndices.has(index)) {
   605→        selectedSilenceIndices.delete(index);
   606→    } else {
   607→        selectedSilenceIndices.add(index);
   608→    }
   609→    updateSelectedCount();
   610→    renderPreviewList();
   611→}
   612→
   613→function updateSelectedCount() {
   614→    if (ui.selectedCount) {
   615→        ui.selectedCount.textContent = selectedSilenceIndices.size;
   616→    }
   617→    if (ui.selectAllSilences) {
   618→        ui.selectAllSilences.checked = selectedSilenceIndices.size === previewSilences.length;
   619→    }
   620→}
   621→
   622→async function seekToTime(seconds) {
   623→    try {
   624→        // Use JSX to set playhead position
   625→        await jsx.evalScript(`app.project.activeSequence.setPlayerPosition("${seconds * 254016000000}")`);
   626→    } catch (e) {
   627→        console.warn('[SPLICE] Seek failed:', e);
   628→    }
   629→}
   630→
   631→// ============================================================================
   632→// CHAPTER LIST
   633→// ============================================================================
   634→function renderChapterList() {
   635→    if (!ui.chapterList) return;
   636→
   637→    const html = currentChapters.map((chapter, i) => `
   638→        <div class="chapter-item" data-index="${i}">
   639→            <div class="chapter-time">${formatTime(chapter.startTime)}</div>
   640→            <div class="chapter-title">${chapter.title}</div>
   641→        </div>
   642→    `).join('');
   643→
   644→    ui.chapterList.innerHTML = html;
   645→
   646→    // Add click handlers
   647→    ui.chapterList.querySelectorAll('.chapter-item').forEach(item => {
   648→        item.addEventListener('click', () => {
   649→            const index = parseInt(item.dataset.index);
   650→            seekToTime(currentChapters[index].startTime);
   651→        });
   652→    });
   653→}
   654→
   655→// ============================================================================
   656→// PREVIEW HANDLERS
   657→// ============================================================================
   658→function initPreviewHandlers() {
   659→    // Select all checkbox
   660→    if (ui.selectAllSilences) {
   661→        ui.selectAllSilences.addEventListener('change', () => {
   662→            if (ui.selectAllSilences.checked) {
   663→                previewSilences.forEach((_, i) => selectedSilenceIndices.add(i));
   664→            } else {
   665→                selectedSilenceIndices.clear();
   666→            }
   667→            updateSelectedCount();
   668→            renderPreviewList();
   669→        });
   670→    }
   671→
   672→    // Apply button
   673→    if (ui.applyPreviewBtn) {
   674→        ui.applyPreviewBtn.addEventListener('click', applyPreviewAndMarkers);
   675→    }
   676→
   677→    // Cancel button
   678→    if (ui.cancelPreviewBtn) {
   679→        ui.cancelPreviewBtn.addEventListener('click', hidePreview);
   680→    }
   681→
   682→    // Build sequence button
   683→    if (ui.buildSequenceBtn) {
   684→        ui.buildSequenceBtn.addEventListener('click', buildSequence);
   685→    }
   686→
   687→    // Copy YouTube timestamps
   688→    if (ui.copyYouTubeBtn) {
   689→        ui.copyYouTubeBtn.addEventListener('click', copyYouTubeTimestamps);
   690→    }
   691→
   692→    // Add chapter markers
   693→    if (ui.addChapterMarkersBtn) {
   694→        ui.addChapterMarkersBtn.addEventListener('click', addChapterMarkers);
   695→    }
   696→}
   697→
   698→async function applyPreviewAndMarkers() {
   699→    if (isOperationInProgress) return;
   700→
   701→    isOperationInProgress = true;
   702→    setStatus('Adding markers...');
   703→
   704→    try {
   705→        // Add silence markers
   706→        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
   707→        for (const silence of selectedSilences) {
   708→            await jsx.call('createMarker', silence.start, 'SPLICE: Silence', silence.end - silence.start, null, 1);
   709→        }
   710→
   711→        // Add take markers
   712→        for (const take of previewTakes) {
   713→            await jsx.call('createMarker', take.start, take.label || `Take ${take.takeNumber}`, take.end - take.start, null, 5);
   714→        }
   715→
   716→        setStatus(`Added ${selectedSilences.length + previewTakes.length} markers`);
   717→    } catch (error) {
   718→        setStatus('Failed to add markers: ' + error.message, true);
   719→    } finally {
   720→        isOperationInProgress = false;
   721→    }
   722→}
   723→
   724→function hidePreview() {
   725→    if (ui.combinedPreview) ui.combinedPreview.classList.add('hidden');
   726→    if (ui.chapterResults) ui.chapterResults.classList.add('hidden');
   727→    if (ui.zoomResults) ui.zoomResults.classList.add('hidden');
   728→    if (ui.resultsEmpty) ui.resultsEmpty.classList.remove('hidden');
   729→    previewSilences = [];
   730→    previewTakes = [];
   731→    selectedSilenceIndices.clear();
   732→}
   733→
   734→async function buildSequence() {
   735→    if (isOperationInProgress) return;
   736→
   737→    isOperationInProgress = true;
   738→    setStatus('Building sequence...');
   739→
   740→    try {
   741→        const settings = getSettings();
   742→
   743→        // Create cut list from selected silences
   744→        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
   745→
   746→        // Call backend to generate cut list
   747→        const cutListResponse = await fetchWithTimeout(
   748→            `${getBackendUrl()}/cut-list`,
   749→            {
   750→                method: 'POST',
   751→                headers: getAuthHeaders(),
   752→                body: JSON.stringify({
   753→                    silences: selectedSilences,
   754→                    takes: previewTakes,
   755→                    enableTakes: settings.enableTakesDetection,
   756→                    jCutOffset: settings.enableJCut ? settings.jcutLeadIn : 0,
   757→                    lCutOffset: settings.enableJCut ? settings.jcutLeadOut : 0
   758→                })
   759→            }
   760→        );
   761→
   762→        if (!cutListResponse.ok) {
   763→            throw new Error(await parseErrorResponse(cutListResponse));
   764→        }
   765→
   766→        const cutList = await cutListResponse.json();
   767→
   768→        // Build sequence via JSX
   769→        const result = await jsx.call('buildSequenceFromCutList', JSON.stringify(cutList));
   770→
   771→        if (result.success) {
   772→            setStatus(`Built sequence: ${result.sequenceName}`);
   773→            hidePreview();
   774→        } else {
   775→            throw new Error(result.error || 'Build failed');
   776→        }
   777→    } catch (error) {
   778→        setStatus('Build failed: ' + error.message, true);
   779→    } finally {
   780→        isOperationInProgress = false;
   781→    }
   782→}
   783→
   784→function copyYouTubeTimestamps() {
   785→    if (currentChapters.length === 0) return;
   786→
   787→    const timestamps = currentChapters.map(ch =>
   788→        `${formatTime(ch.startTime)} ${ch.title}`
   789→    ).join('\n');
   790→
   791→    navigator.clipboard.writeText(timestamps).then(() => {
   792→        setStatus('Copied YouTube timestamps');
   793→    }).catch(() => {
   794→        setStatus('Failed to copy', true);
   795→    });
   796→}
   797→
   798→async function addChapterMarkers() {
   799→    if (isOperationInProgress || currentChapters.length === 0) return;
   800→
   801→    isOperationInProgress = true;
   802→    setStatus('Adding chapter markers...');
   803→
   804→    try {
   805→        for (const chapter of currentChapters) {
   806→            await jsx.call('addChapterMarker', chapter.startTime, chapter.title, chapter.description);
   807→        }
   808→        setStatus(`Added ${currentChapters.length} chapter markers`);
   809→    } catch (error) {
   810→        setStatus('Failed to add markers: ' + error.message, true);
   811→    } finally {
   812→        isOperationInProgress = false;
   813→    }
   814→}
   815→
   816→// ============================================================================
   817→// MODALS
   818→// ============================================================================
   819→function initModals() {
   820→    // Settings modal
   821→    if (ui.settingsBtn && ui.settingsModal) {
   822→        ui.settingsBtn.addEventListener('click', () => {
   823→            ui.settingsModal.classList.remove('hidden');
   824→        });
   825→    }
   826→
   827→    if (ui.closeSettingsBtn && ui.settingsModal) {
   828→        ui.closeSettingsBtn.addEventListener('click', () => {
   829→            ui.settingsModal.classList.add('hidden');
   830→        });
   831→    }
   832→
   833→    // Login modal
   834→    if (ui.creditBadge) {
   835→        ui.creditBadge.addEventListener('click', () => {
   836→            const settings = getSettings();
   837→            if (!settings.customerId) {
   838→                showLoginModal();
   839→            }
   840→        });
   841→    }
   842→
   843→    if (ui.closeLoginBtn && ui.loginModal) {
   844→        ui.closeLoginBtn.addEventListener('click', () => {
   845→            ui.loginModal.classList.add('hidden');
   846→        });
   847→    }
   848→
   849→    if (ui.saveLoginBtn) {
   850→        ui.saveLoginBtn.addEventListener('click', activateLicense);
   851→    }
   852→
   853→    // Close modals on backdrop click
   854→    document.querySelectorAll('.modal').forEach(modal => {
   855→        modal.addEventListener('click', (e) => {
   856→            if (e.target === modal) {
   857→                modal.classList.add('hidden');
   858→            }
   859→        });
   860→    });
   861→}
   862→
   863→function showLoginModal() {
   864→    if (ui.loginModal) ui.loginModal.classList.remove('hidden');
   865→}
   866→
   867→async function activateLicense() {
   868→    if (!ui.licenseKeyInput) return;
   869→
   870→    const licenseKey = ui.licenseKeyInput.value.trim();
   871→    if (!licenseKey) {
   872→        setStatus('Please enter a license key', true);
   873→        return;
   874→    }
   875→
   876→    try {
   877→        const response = await fetchWithTimeout(
   878→            `${getBackendUrl()}/license/activate`,
   879→            {
   880→                method: 'POST',
   881→                headers: { 'Content-Type': 'application/json' },
   882→                body: JSON.stringify({ licenseKey })
   883→            }
   884→        );
   885→
   886→        if (!response.ok) {
   887→            throw new Error(await parseErrorResponse(response));
   888→        }
   889→
   890→        const data = await response.json();
   891→        saveSettings({
   892→            customerId: data.customerId,
   893→            licenseKey: licenseKey
   894→        });
   895→
   896→        if (ui.loginModal) ui.loginModal.classList.add('hidden');
   897→        setStatus('License activated');
   898→        updateCredits();
   899→    } catch (error) {
   900→        setStatus('Activation failed: ' + error.message, true);
   901→    }
   902→}
   903→
   904→// ============================================================================
   905→// CREDITS
   906→// ============================================================================
   907→function initCredits() {
   908→    updateCredits();
   909→}
   910→
   911→async function updateCredits() {
   912→    if (!ui.creditBadge) return;
   913→
   914→    const settings = getSettings();
   915→    if (!settings.customerId) {
   916→        ui.creditBadge.className = 'credit-badge login';
   917→        ui.creditBadge.textContent = 'Login';
   918→        ui.creditBadge.style.display = 'flex';
   919→        return;
   920→    }
   921→
   922→    try {
   923→        const response = await fetchWithTimeout(
   924→            `${getBackendUrl()}/credits`,
   925→            {
   926→                method: 'GET',
   927→                headers: getAuthHeaders()
   928→            }
   929→        );
   930→
   931→        if (response.ok) {
   932→            const data = await response.json();
   933→            const hours = (data.remainingMinutes / 60).toFixed(1);
   934→            ui.creditBadge.textContent = `${hours}h`;
   935→            ui.creditBadge.className = data.remainingMinutes < 60 ? 'credit-badge low' : 'credit-badge ok';
   936→            ui.creditBadge.style.display = 'flex';
   937→        } else {
   938→            ui.creditBadge.className = 'credit-badge error';
   939→            ui.creditBadge.textContent = 'Error';
   940→            ui.creditBadge.style.display = 'flex';
   941→        }
   942→    } catch {
   943→        ui.creditBadge.className = 'credit-badge error';
   944→        ui.creditBadge.textContent = 'Offline';
   945→        ui.creditBadge.style.display = 'flex';
   946→    }
   947→}
   948→
   949→// Export for debugging
   950→window.spliceDebug = {
   951→    getState: () => ({
   952→        silences: previewSilences,
   953→        takes: previewTakes,
   954→        chapters: currentChapters,
   955→        zooms: currentZooms,
   956→        selected: Array.from(selectedSilenceIndices)
   957→    }),
