    67→    return { wavPath: WAV_PATH, xmlPath: XML_PATH, presetPath: WAV_PRESET_PATH };
    68→  } catch (err) {
    69→    console.error('[SPLICE Config] Failed to initialize paths:', err);
    70→
    71→    // Fallback to platform-specific temp paths
