   150→// =============================================================================
   151→// Server Configuration
   152→// =============================================================================
   153→
   154→const app = express();
   155→const PORT = process.env.PORT || 3847;
   156→
   157→// HTTPS certificates (generated by mkcert) - only for local development
   158→let httpsOptions = null;
   159→if (!isProduction) {
   160→  const keyPath = path.join(__dirname, 'localhost+1-key.pem');
   161→  const certPath = path.join(__dirname, 'localhost+1.pem');
   162→  if (fs.existsSync(keyPath) && fs.existsSync(certPath)) {
   163→    httpsOptions = {
   164→      key: fs.readFileSync(keyPath),
   165→      cert: fs.readFileSync(certPath)
   166→    };
   167→  }
   168→}
   169→
   170→// =============================================================================
   171→// Security Configuration
   172→// =============================================================================
   173→
   174→// CORS whitelist - restrict origins in production
   175→const CORS_WHITELIST = [
   176→  'http://localhost:3000',
   177→  'http://localhost:3847',
   178→  'https://localhost:3847',
   179→  'http://127.0.0.1:3000',
   180→  'http://127.0.0.1:3847',
   181→  'https://127.0.0.1:3847',
   182→  'https://splice.app',
   183→  'https://www.splice.app',
   184→  'https://splice-api-production.up.railway.app',
   185→  // Adobe CEP/UXP panels run from file:// or bolt://
   186→  'file://',
   187→  'bolt://'
   188→];
   189→
   190→const corsOptions = {
   191→  origin: function (origin, callback) {
   192→    // Allow requests with no origin (like mobile apps, Postman, or Adobe plugins)
   193→    if (!origin) {
   194→      return callback(null, true);
   195→    }
   196→    // Check if origin is in whitelist
   197→    if (CORS_WHITELIST.some(allowed => origin.startsWith(allowed) || origin === allowed)) {
   198→      return callback(null, true);
   199→    }
   200→    // In development, allow all origins with warning
   201→    if (!isProduction) {
   202→      console.warn(`[CORS] Non-whitelisted origin in dev: ${origin}`);
   203→      return callback(null, true);
   204→    }
   205→    // In production, reject non-whitelisted origins
   206→    console.error(`[CORS] Blocked request from: ${origin}`);
   207→    return callback(new Error('Not allowed by CORS'));
   208→  },
   209→  credentials: true,
   210→  methods: ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS'],
   211→  allowedHeaders: ['Content-Type', 'Authorization', 'x-stripe-customer-id', 'stripe-signature']
   212→};
   213→
   214→app.use(cors(corsOptions));
   215→
   216→// Security headers via helmet
   217→app.use(helmet({
   218→  contentSecurityPolicy: {
   219→    directives: {
   220→      defaultSrc: ["'self'"],
   221→      scriptSrc: ["'self'"],
   222→      styleSrc: ["'self'", "'unsafe-inline'"],
   223→      imgSrc: ["'self'", 'data:', 'https:'],
   224→      connectSrc: ["'self'", 'https://api.stripe.com', 'https://api.openai.com', 'https://api.replicate.com'],
   225→      fontSrc: ["'self'"],
   226→      objectSrc: ["'none'"],
   227→      mediaSrc: ["'self'"],
   228→      frameSrc: ["'none'"]
   229→    }
   230→  },
   231→  hsts: {
   232→    maxAge: 31536000,
   233→    includeSubDomains: true,
   234→    preload: true
   235→  },
   236→  referrerPolicy: { policy: 'strict-origin-when-cross-origin' },
   237→  noSniff: true,
   238→  xssFilter: true,
   239→  hidePoweredBy: true
   240→}));
   241→
   242→// Helper to determine tier from price ID with logging
   243→// Supports both monthly and annual pricing
   244→function getTierFromPriceId(priceId) {
   245→  // Monthly prices
   246→  if (priceId === process.env.STRIPE_PRICE_STARTER) return 'starter';
   247→  if (priceId === process.env.STRIPE_PRICE_PRO) return 'pro';
   248→  if (priceId === process.env.STRIPE_PRICE_TEAM) return 'team';
   249→
   250→  // Annual prices (same tier, just different billing period)
   251→  if (priceId === process.env.STRIPE_PRICE_STARTER_ANNUAL) return 'starter';
   252→  if (priceId === process.env.STRIPE_PRICE_PRO_ANNUAL) return 'pro';
   253→  if (priceId === process.env.STRIPE_PRICE_TEAM_ANNUAL) return 'team';
   254→
   255→  // Log unknown price ID for debugging
   256→  console.warn(`[SPLICE] Unknown price ID: ${priceId} - defaulting to starter tier`);
   257→  return 'starter';
   258→}
