   260→// =============================================================================
   261→// Stripe Webhook (must be before express.json())
   262→// =============================================================================
   263→
   264→app.post('/webhooks/stripe', express.raw({ type: 'application/json' }), async (req, res) => {
   265→  const sig = req.headers['stripe-signature'];
   266→  const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET;
   267→
   268→  let event;
   269→
   270→  try {
   271→    if (webhookSecret) {
   272→      event = stripe.webhooks.constructEvent(req.body, sig, webhookSecret);
   273→    } else if (isProduction) {
   274→      // SECURITY: Reject unsigned webhooks in production
   275→      console.error('[SPLICE] CRITICAL: STRIPE_WEBHOOK_SECRET not set in production');
   276→      return res.status(500).json({ error: 'Webhook configuration error: secret not configured' });
   277→    } else {
   278→      // For local development testing only
   279→      // req.body is a Buffer from express.raw(), convert to string for JSON.parse
   280→      const bodyString = typeof req.body === 'string' ? req.body : req.body.toString('utf8');
   281→      event = JSON.parse(bodyString);
   282→      console.warn('[SPLICE] Warning: Processing webhook without signature verification (dev only)');
   283→    }
   284→  } catch (err) {
   285→    console.error('[SPLICE] Webhook signature verification failed:', err.message);
   286→    return res.status(400).json({ error: 'Webhook signature verification failed' });
   287→  }
   288→
   289→  console.log(`[SPLICE] Webhook received: ${event.type} (${event.id})`);
   290→
   291→  // Idempotency check - skip if already processed
   292→  if (await usageTracking.isEventProcessed(event.id)) {
   293→    console.log(`[SPLICE] Event ${event.id} already processed, skipping`);
   294→    return res.json({ received: true, skipped: true });
   295→  }
   296→
   297→  try {
   298→    switch (event.type) {
   299→      case 'customer.subscription.created':
   300→      case 'customer.subscription.updated': {
   301→        const subscription = event.data.object;
   302→        const customerId = subscription.customer;
   303→
   304→        // Validate customerId
   305→        if (!customerId) {
   306→          console.error('[SPLICE] Missing customer ID in subscription event');
   307→          return res.status(400).json({ error: 'Missing customer ID' });
   308→        }
   309→
   310→        // Get tier from price ID
   311→        const priceId = subscription.items?.data?.[0]?.price?.id;
   312→        const tier = getTierFromPriceId(priceId);
   313→
   314→        // Update user tier and reset hours
   315→        await usageTracking.updateTier(customerId, tier);
   316→        console.log(`[SPLICE] Updated customer ${customerId} to tier: ${tier}`);
   317→
   318→        // Generate license key for new subscriptions with retry and delivery
   319→        if (event.type === 'customer.subscription.created') {
   320→          let licenseResult = null;
   321→          let retryCount = 0;
   322→          const maxRetries = 3;
   323→
   324→          // Retry mechanism for license key generation
   325→          while (retryCount < maxRetries) {
   326→            licenseResult = await licenseService.generateLicenseKey(customerId);
   327→            if (licenseResult.success) {
   328→              break;
   329→            }
   330→            retryCount++;
   331→            console.warn(`[SPLICE] License key generation attempt ${retryCount}/${maxRetries} failed: ${licenseResult.error}`);
   332→            // Wait before retry (exponential backoff)
   333→            if (retryCount < maxRetries) {
   334→              await new Promise(resolve => setTimeout(resolve, 1000 * retryCount));
   335→            }
   336→          }
   337→
   338→          if (licenseResult && licenseResult.success) {
   339→            // SECURITY: Mask license key in logs
   340→            console.log(`[SPLICE] Generated license key for ${maskSensitiveData(customerId)}: ${maskSensitiveData(licenseResult.key)}`);
   341→
   342→            // Store license key in Stripe subscription metadata as backup
   343→            try {
   344→              await stripe.subscriptions.update(subscription.id, {
   345→                metadata: {
   346→                  license_key: licenseResult.key,
   347→                  license_generated_at: new Date().toISOString()
   348→                }
   349→              });
   350→              console.log(`[SPLICE] Stored license key in Stripe metadata for subscription ${subscription.id}`);
   351→            } catch (stripeErr) {
   352→              console.error(`[SPLICE] Failed to store license key in Stripe metadata:`, stripeErr.message);
   353→            }
   354→
   355→            // Get customer email and send license key
   356→            try {
   357→              const customer = await stripe.customers.retrieve(customerId);
   358→              if (customer.email) {
   359→                // SECURITY: Mask sensitive data in logs
   360→                console.log(`[SPLICE] License key ready for delivery to ${maskSensitiveData(customer.email)}: ${maskSensitiveData(licenseResult.key)}`);
   361→                // TODO: Integrate with email service (SendGrid, SES, etc.)
   362→                // await sendLicenseKeyEmail(customer.email, licenseResult.key, tier);
   363→
   364→                // Store email in database for reference
   365→                await usageTracking.updateTier(customerId, tier, customer.email);
   366→              } else {
   367→                console.warn(`[SPLICE] No email found for customer ${customerId}`);
   368→              }
   369→            } catch (emailErr) {
   370→              console.error(`[SPLICE] Error getting customer email:`, emailErr.message);
   371→            }
   372→          } else {
   373→            // CRITICAL: License generation failed - return 500 to trigger Stripe retry
   374→            const errorMsg = `Failed to generate license key after ${maxRetries} attempts: ${licenseResult?.error || 'Unknown error'}`;
   375→            console.error(`[SPLICE] ${errorMsg}`);
   376→            return res.status(500).json({ error: errorMsg });
   377→          }
   378→        }
   379→        break;
   380→      }
   381→
   382→      case 'customer.subscription.deleted': {
   383→        const subscription = event.data.object;
   384→        const customerId = subscription.customer;
   385→
   386→        // Validate customerId
   387→        if (!customerId) {
   388→          console.error('[SPLICE] Missing customer ID in subscription.deleted event');
   389→          return res.status(400).json({ error: 'Missing customer ID' });
   390→        }
   391→
   392→        // Downgrade to cancelled (0 hours)
   393→        await usageTracking.updateTier(customerId, 'cancelled');
   394→        console.log(`[SPLICE] Subscription cancelled for customer ${customerId}`);
   395→        break;
   396→      }
   397→
   398→      case 'invoice.payment_succeeded': {
   399→        const invoice = event.data.object;
   400→        const customerId = invoice.customer;
   401→        const subscriptionId = invoice.subscription;
   402→
   403→        // Validate customerId
   404→        if (!customerId) {
   405→          console.error('[SPLICE] Missing customer ID in invoice event');
   406→          return res.status(400).json({ error: 'Missing customer ID' });
   407→        }
   408→
   409→        // Reset hours on successful payment (new billing period)
   410→        let tier = 'starter';
   411→        if (subscriptionId) {
   412→          const subscription = await stripe.subscriptions.retrieve(subscriptionId);
   413→          const priceId = subscription.items?.data?.[0]?.price?.id;
   414→          tier = getTierFromPriceId(priceId);
   415→
   416→          await usageTracking.resetHours(customerId, tier);
   417→          console.log(`[SPLICE] Reset hours for customer ${customerId} (tier: ${tier})`);
   418→        }
   419→
   420→        // Check for affiliate coupon and record commission
   421→        const discount = invoice.discount;
   422→        if (discount && discount.coupon) {
   423→          const couponId = discount.coupon.id;
   424→          // Check if this is an affiliate code (like JIMMYN)
   425→          if (referralService.AFFILIATE_CODES[couponId]) {
   426→            const amountPaid = invoice.amount_paid / 100; // Convert cents to dollars
   427→            await referralService.recordAffiliateCommission(
   428→              couponId,
   429→              customerId,
   430→              amountPaid,
   431→              tier
   432→            );
   433→            console.log(`[SPLICE] Recorded affiliate commission for ${couponId}`);
   434→          }
   435→        }
   436→        break;
   437→      }
   438→
   439→      case 'invoice.payment_failed': {
   440→        const invoice = event.data.object;
   441→        const customerId = invoice.customer;
   442→        const attemptCount = invoice.attempt_count || 1;
   443→
   444→        // Validate customerId
   445→        if (!customerId) {
   446→          console.error('[SPLICE] Missing customer ID in payment_failed event');
   447→          return res.status(400).json({ error: 'Missing customer ID' });
   448→        }
   449→
   450→        console.warn(`[SPLICE] Payment failed for customer ${customerId} (attempt ${attemptCount})`);
   451→
   452→        // Stripe will retry automatically per retry settings
   453→        // On final failure, Stripe will cancel subscription which triggers customer.subscription.deleted
   454→        // For now, just log and potentially notify user
   455→        if (attemptCount >= 3) {
   456→          console.error(`[SPLICE] Final payment attempt failed for customer ${customerId}`);
   457→          // TODO: Send warning email to customer about impending cancellation
   458→        }
   459→        break;
   460→      }
   461→
   462→      case 'customer.deleted': {
   463→        const customer = event.data.object;
   464→        const customerId = customer.id;
   465→
   466→        // Validate customerId
   467→        if (!customerId) {
   468→          console.error('[SPLICE] Missing customer ID in customer.deleted event');
   469→          return res.status(400).json({ error: 'Missing customer ID' });
   470→        }
   471→
   472→        // Clean up user data - downgrade to cancelled
   473→        await usageTracking.updateTier(customerId, 'cancelled');
   474→        console.log(`[SPLICE] Customer deleted: ${customerId}`);
   475→        break;
   476→      }
   477→
   478→      default:
   479→        console.log(`[SPLICE] Unhandled event type: ${event.type}`);
   480→    }
   481→
   482→    // Record event as processed (idempotency)
   483→    await usageTracking.recordWebhookEvent(event.id, event.type);
   484→
   485→    res.json({ received: true });
   486→  } catch (err) {
   487→    console.error('[SPLICE] Webhook handler error:', err);
   488→    res.status(500).json({ error: err.message });
   489→  }
   490→});
