   542→// Legacy XML processing endpoint (backwards compatibility)
   543→const { processXMLFile } = require('./services/xmlProcessor');
   544→app.post('/process-xml', requireCredits({ endpoint: 'process-xml' }), async (req, res) => {
   545→  const {
   546→    xmlPath,
   547→    silences,
   548→    removeGaps = true,
   549→    outputPath = null
   550→  } = req.body;
   551→
   552→  if (!xmlPath) {
   553→    return res.status(400).json({ error: 'xmlPath is required' });
   554→  }
   555→
   556→  if (!silences || !Array.isArray(silences)) {
   557→    return res.status(400).json({ error: 'silences array is required' });
   558→  }
   559→
   560→  if (!fs.existsSync(xmlPath)) {
   561→    return res.status(404).json({ error: `XML file not found: ${xmlPath}` });
   562→  }
   563→
   564→  console.log(`[SPLICE] Processing XML: ${xmlPath} with ${silences.length} silence(s)`);
   565→
   566→  try {
   567→    const result = await processXMLFile(xmlPath, silences, {
   568→      outputPath,
   569→      removeGaps
   570→    });
   571→
   572→    res.json({
   573→      success: true,
   574→      inputPath: xmlPath,
   575→      outputPath: result.outputPath,
   576→      stats: result.stats
   577→    });
