/* global performance */
/**
 * SPLICE v3.5 Comprehensive E2E Test Suite
 * Tests all newly implemented features end-to-end
 */

const https = require('https');
const fs = require('fs');

// Disable SSL verification for local testing
process.env.NODE_TLS_REJECT_UNAUTHORIZED = '0';

const BASE_URL = 'https://127.0.0.1:3847';
const TEST_CUSTOMER_ID = 'cus_test_comprehensive_123';

// Test results tracking
const results = {
  passed: 0,
  failed: 0,
  errors: [],
  warnings: [],
  performance: {}
};

// Helper to make HTTP requests
function makeRequest(method, endpoint, body = null, headers = {}) {
  return new Promise((resolve, reject) => {
    const url = new URL(endpoint, BASE_URL);
    const options = {
      hostname: url.hostname,
      port: url.port,
      path: url.pathname + url.search,
      method,
      headers: {
        'Content-Type': 'application/json',
        'x-stripe-customer-id': TEST_CUSTOMER_ID,
        ...headers
      },
      rejectUnauthorized: false
    };

    const req = https.request(options, (res) => {
      let data = '';
      res.on('data', chunk => data += chunk);
      res.on('end', () => {
        try {
          const json = data ? JSON.parse(data) : {};
          resolve({ status: res.statusCode, data: json, headers: res.headers });
        } catch (_e) {
          resolve({ status: res.statusCode, data: data, headers: res.headers });
        }
      });
    });

    req.on('error', reject);

    if (body) {
      req.write(JSON.stringify(body));
    }
    req.end();
  });
}

function pass(test) {
  results.passed++;
  console.log(`  ✓ ${test}`);
}

function fail(test, error) {
  results.failed++;
  results.errors.push({ test, error });
  console.log(`  ✗ ${test}: ${error}`);
}

function warn(message) {
  results.warnings.push(message);
  console.log(`  ⚠ ${message}`);
}

// Note: benchmark function available but not currently used in main tests
// function benchmark(name, fn) {
//   const start = performance.now();
//   const result = fn();
//   const duration = performance.now() - start;
//   results.performance[name] = duration;
//   return { result, duration };
// }

